import { Button, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'

const Register = () => {
  const formInitialVal = { userName: '', email: '', password: '' }
  const [initialValue, setInitialValue] = useState(formInitialVal)
  const [formErrors, setFormErrors] = useState({})

  const formValidationMsg = (values) => {
    const errors = {};
    if (!values.userName) {
      errors.userName = "This filed is required."
    } else if (values.userName.length < 3) {
      errors.userName = "User name should be minimum 3 characters."
    }
  }

  const handleChange = (e) => {
    const {name, value} = e.target;
    setInitialValue({ ...initialValue, [name]: value })
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const errors = formValidationMsg(initialValue)
    setFormErrors(errors)
    console.log(formErrors)
    // console.log("ðŸš€ ~ file: index.js:13 ~ Register ~ initialValue:", initialValue)
    // const data = new FormData(event.currentTarget);
    // console.log({
    //   userName: data.get('userName'),
    //   email: data.get('email'),
    //   password: data.get('password'),
    //   cnfPassword: data.get('cnfPassword')
    // });
  };

  return (
    <form style={{width: '100%'}}>
        <Typography component="h1" variant="h5">
        Sign Up
        </Typography>
        <TextField
        margin="normal"
        required
        fullWidth
        id="text"
        label="User Name"
        name="userName"
        autoComplete="text"
        // autoFocus
        // value={initialValue.userName}
        onChange={handleChange}
        />
        <TextField
        margin="normal"
        required
        fullWidth
        id="email"
        label="Email Address"
        name="email"
        autoComplete="email"
        />
        <TextField
        margin="normal"
        required
        fullWidth
        name="password"
        label="Password"
        type="password"
        id="password"
        autoComplete="current-password"
        />
        <TextField
        margin="normal"
        required
        fullWidth
        name="cnfPassword"
        label="Confirm Password"
        type="password"
        id="cnfPassword"
        autoComplete="confirm-password"
        />
        <Button
        type="submit"
        fullWidth
        variant="contained"
        sx={{ mt: 3, mb: 2 }}
        onClick={handleSubmit}
        >
        Sign Up
        </Button>
    </form>
  )
}

export default Register
