import { Button, InputAdornment, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'
import axios from 'axios';

const Register = () => {

  const formInitialVal = { name: '', email: '', phone: '', password: '' }
  const [initialValue, setInitialValue] = useState(formInitialVal)
  const [formErrors, setFormErrors] = useState({});

  const validateMsg = (values) => {
    const errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

    if (!values.name) {
      errors.name = "This filed is required."
    } else if (values.name.length < 3) {
      errors.name = "User name should be minimum 3 characters."
    }

    if (!values.email) {
      errors.email = "This filed is required."
    } else if (!emailRegex.test(values.email)) {
      errors.email = "This is not a valid email."
    }

    if (!values.phone) {
      errors.phone = "This filed is required."
    } else if (values.phone.length < 10) {
      errors.phone = "Mobile number should be minimum 10 digits."
    }

    if (!values.password) {
      errors.password = "This filed is required."
    } else if (values.password.length < 8) {
      errors.password = "Password should be minimum 8 characters."
    } else if (!passwordRegex.test(values.password)) {
      errors.password = "Password format should be Abcd@123"
    }

    return errors;
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInitialValue({ ...initialValue, [name]: value })
  }

  const handleSubmit = () => {
    const errors = validateMsg(initialValue)
    setFormErrors(errors)
    console.log(errors)
    if (Object.keys(errors)?.length) {
      return false
    }
    // console.log(initialValue)
    axios.post(`${'http://localhost:3100/api/register'}`, initialValue)
      .then(res => {
        console.log("ðŸš€ ~ file: index.js:60 ~ handleSubmit ~ res:", res)
        if (res.status === 200) {
          setInitialValue(formInitialVal)
          setFormErrors({})
        }
      })
      .catch(err => console.log(err))
  };

  return (
    <form style={{ width: '100%' }}>
      <Typography component="h1" variant="h5">
        Sign Up
      </Typography>
      <TextField
        margin="normal"
        required
        fullWidth
        id="text"
        label="User Name"
        name="name"
        autoComplete="off"
        autoFocus
        error={Boolean(formErrors.name)}
        helperText={formErrors.name}
        value={initialValue.name}
        onChange={handleChange}
      />
      <TextField
        margin="normal"
        required
        fullWidth
        id="email"
        label="Email Address"
        name="email"
        autoComplete="off"
        error={Boolean(formErrors.email)}
        helperText={formErrors.email}
        value={initialValue.email}
        onChange={handleChange}
      />
      <TextField
        margin="normal"
        required
        fullWidth
        id="phone"
        label="Enter Mobile Number"
        name="phone"
        autoComplete="off"
        InputProps={{
          startAdornment: <InputAdornment position='start'>+91</InputAdornment>
        }}
        error={Boolean(formErrors.phone)}
        helperText={formErrors.phone}
        value={initialValue.phone}
        onChange={handleChange}
      />
      <TextField
        margin="normal"
        required
        fullWidth
        name="password"
        label="Password"
        type="password"
        id="password"
        autoComplete="off"
        error={Boolean(formErrors.password)}
        helperText={formErrors.password}
        value={initialValue.password}
        onChange={handleChange}
      />
      {/* <TextField
        margin="normal"
        required
        fullWidth
        name="cnfPassword"
        label="Confirm Password"
        type="password"
        id="cnfPassword"
        autoComplete="confirm-password"
        /> */}
      <Button
        type="button"
        fullWidth
        variant="contained"
        sx={{ mt: 3, mb: 2 }}
        onClick={handleSubmit}
      >
        Sign Up
      </Button>
    </form>
  )
}

export default Register
