import { Button, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'

const Register = () => {

  const formInitialVal = { userName: '', email: '', password: '' }
  const [initialValue, setInitialValue] = useState(formInitialVal)

  const handleChange = (e) => {
    const {name, value} = e.target;
    setInitialValue({ ...initialValue, [name]: value })
    console.log(initialValue)
  }

  const handleSubmit = () => {
    console.log("Clicked")
  };

  return (
    <form style={{width: '100%'}}>
        <Typography component="h1" variant="h5">
        Sign Up
        </Typography>
        <TextField
        margin="normal"
        required
        fullWidth
        id="text"
        label="User Name"
        name="userName"
        autoComplete="off"
        // autoFocus
        // value={initialValue.userName}
        // error={Boolean(formErrors.userName)}
        // helperText={formErrors.userName}
        onChange={handleChange}
        />
        <TextField
        margin="normal"
        required
        fullWidth
        id="email"
        label="Email Address"
        name="email"
        autoComplete="off"
        onChange={handleChange}
        />
        <TextField
        margin="normal"
        required
        fullWidth
        name="password"
        label="Password"
        type="password"
        id="password"
        autoComplete="off"
        onChange={handleChange}
        />
        {/* <TextField
        margin="normal"
        required
        fullWidth
        name="cnfPassword"
        label="Confirm Password"
        type="password"
        id="cnfPassword"
        autoComplete="confirm-password"
        /> */}
        <Button
        type="button"
        fullWidth
        variant="contained"
        sx={{ mt: 3, mb: 2 }}
        onClick={handleSubmit}
        >
        Sign Up
        </Button>
    </form>
  )
}

export default Register






