import * as React from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Link, Outlet, useSearchParams } from "react-router-dom";

// function Copyright(props) {
//   return (
//     <Typography variant="body2" color="text.secondary" align="center" {...props}>
//       {'Copyright © '}
//       <Link color="inherit" href="https://mui.com/">
//         Your Website
//       </Link>{' '}
//       {new Date().getFullYear()}
//       {'.'}
//     </Typography>
//   );
// }

// TODO remove, this demo shouldn't need to reset the theme.

const defaultTheme = createTheme();

export default function AuthScreen() {
  const [navParams, setNavParams] = useSearchParams()
  const linkText = navParams.get('register') === 'register'
  const [nav, setNav] = React.useState(false)

  const linkToLogin = () => {
    setNav(false)
  }

  const linkToRegister = () => {
    setNav(true)
  }

  return (
    <ThemeProvider theme={defaultTheme}>
      <Grid container sx={{ height: '100vh' }}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          sx={{
            backgroundImage: 'url(https://source.unsplash.com/random?wallpapers)',
            backgroundRepeat: 'no-repeat',
            backgroundColor: (t) =>
              t.palette.mode === 'light' ? t.palette.grey[50] : t.palette.grey[900],
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        >
        </Grid>
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <Box
            sx={{
              my: 8,
              mx: 4,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
            }}
          >
            <Outlet />
            <Grid container>
              <Grid item xs>
                <Link>
                  Forgot password?
                </Link>
              </Grid>
              <Grid item>
                {
                  nav ? (
                    <>I have an account. <Link to="login" onClick={linkToLogin}>Login</Link></>
                  ) : (
                    <>Don't have an account? <Link to="register" onClick={linkToRegister}>{linkText}</Link></>
                  )
                }
              </Grid>
            </Grid>
            {/* <Copyright sx={{ mt: 5 }} /> */}
          </Box>
        </Grid>
      </Grid>
    </ThemeProvider>
  );
}
