import React from 'react'
import styles from './Header.module.scss'
import logo from '../../assets/react.svg'
import profileImg from '../../assets/doctor.jpg'
import { Avatar, Button, MenuItem } from '@mui/material'

const Header = () => {
  let isLogedIn = true;

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };


  return (
    <header className={styles['header-top']}>
      <div className='container d-flex flex-nowrap align-items-center'>
        <span className={styles['company-logo']}>
          <img src={logo} alt="logo" width="50" height="50" />
        </span>
        <span className='flex-1'></span>
        <div className='d-flex flex-nowrap align-items-center gap-8'>
          {isLogedIn
            ? <>
                <Avatar alt="Remy Sharp" src={profileImg} />
                <Button onClick={handleClick}>Riya Sinha</Button>
              </>
            : <Button variant="outlined">Login</Button>
          }
        </div>

        <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
      >
        <MenuItem onClick={handleClose}>Profile</MenuItem>
        <MenuItem onClick={handleClose}>My account</MenuItem>
        <MenuItem onClick={handleClose}>Logout</MenuItem>
      </Menu>

      </div>
    </header>
  )
}

export default Header
