import React, { useState } from 'react'
import { Button } from '@mui/material'
import { DialogBox } from '../genericComponent/DialogBox'
import EmployeeForm from './EmployeeForm'
// import shortid from 'shortid';


const cities = ['delhi', 'mumbai', 'banglore', 'kolkata', 'pune'];
const skills = ['angular', 'react', 'vue', 'nextjs', 'drupal', 'html5', 'css3', 'sass', 'mui', 'material-ui'];

const Employee = () => {
  const [addEmp, setAddEmp] = useState(false)
  const addEmployeeBtn = () => { setAddEmp(!addEmp) }
const [error, setError] = useState({
  name:''
})
  const [empData, setEmpData] = useState({
    fullName: "",
    email: "",
    contact: "",
    dob: "",
    city: "",
    skill: [],
    gender: "",
    isActive: ""
  });

  const handleChange = (key, value) => {
    setEmpData((prevValue) => ({...prevValue, [key]: value}));
}
const handleValidation = () =>{
    if(empData.fullName === ''){
    setError({...error, name:'plz'})
    ret
    }
}

  const handleSubmit = (e) =>{
    e.preventDefault();
    handleValidation();
    // console.log(empData)
  }

  return (
    <>
      <div className='d-flex align-items-center justify-content-between'>
        <h1>Employee List</h1>
        <Button variant="contained" onClick={()=>addEmployeeBtn()}>Add Employee</Button>
        <DialogBox
          maxWidth="md" 
          title={'Add Employee'} 
          openModal={addEmp} 
          closeModel={addEmployeeBtn} 
          onSubmit={handleSubmit}
          modelData={
            <EmployeeForm 
            cities={cities}
            skills={skills}
            values={empData}
            handleChange={handleChange} 
            // errors={errors}
            // handleBlur={handleBlur}
            />
          } 
        />
      </div>
      <ul>
      {/* {
        allData.map((data) => (
          <li key={data.id}>{data.fullName}</li>
        ))
      } */}

      </ul>
    </>
  )
}

