import React, { useState } from 'react'

const Form = () => {

    const [todo, setTodo] = useState()

    const handelChange = (value) => {
        console.log("firstName", value.target.value)
    }

    return(
        <>
            <form>
                <input type="text" 
                onChange={(e)=>handelChange(e)} 
                value={todo.firstName} />
            </form>
        </>
    )
}

export default Form


