import React, {useState} from 'react';

const Counter = () => {
    const [count, setCount] = useState(0);
    let incActive = false;

    const increment = () => {
        if(count < 10){
            setCount(count + 1);
            if((count+1) === 10){
                isActive = true;
                console.log("active")
            }
        }
    }

    const decrement = () => {
        if(count > 0){
            setCount(count - 1)
            if((count-1) === 0){
                isActive = true;
                console.log("active")
            }
        }
    }

    return(
        <>
        <h1>Counter is: {count}</h1>
        <button disabled={isActive} onClick={increment}>Increment</button>
        <button disabled={isActive} onClick={decrement}>Decrement</button>
        </>
    )
}

export default Counter
