import React, {useState} from 'react';

const Counter = () => {
    const [count, setCount] = useState(0);

    const increment = () => {
        // console.log("Hello")
        setCount(count+1)
    }

    return(
        <>
        <h1>Counter is: {count}</h1>
        <button onClick={increment}>Increment</button>
        </>
    )
}

export default Counter
