import React, {useRef, useEffect} from 'react'

const AboutUsChild = () => {

  const firstNameRef = useRef(null);
  const lastNameRef = useRef(null);
  const submitRef = useRef(null);

  useEffect(()=>{
    firstNameRef.current.focus();
  }, [])

  function firstKeyDown(e) {
    if (e.key === "Enter") {
      firstNameRef.current.focus();
    }
  }

  function lastKeyDown(e) {
    if (e.key === 'Enter') {
      lastNameRef.current.focus();
    }
  }

  function submitKeyDown(e) {
    alert("Form Value")
  }

  return (
    <div>
      <h1>About Us Form</h1>
      <form>
        {/* <div>
          <label htmlFor="firstName">First Name</label><br />
          <input id="firstName" type="text" onKeyDown={firstKeyDown} ref={firstNameRef} />
        </div>
        <div>
          <label htmlFor="lastName">Last Name</label><br />
          <input id="lastName" type="text" onKeyDown={lastKeyDown} ref={lastNameRef} />
        </div> */}

        <input id="firstName" type="text" onKeyDown={firstKeyDown} ref={firstNameRef} />
        <input id="lastName" type="text" onKeyDown={lastKeyDown} ref={lastNameRef} />
        <button onKeyDown={submitKeyDown} ref={submitRef}>Button</button>
      </form>
    </div>
  )
}

export default AboutUsChild
