import React from 'react';

const MessageList = (props) => {
    const {messageList} = props;

    // const [inputValue, setInputValue] = useState('');
    // const [getId, setGetId] = useState(null);

    const handelDelete = (getId) => {
        const indexValue = messageList.findIndex((data)=>(data.id === getId))
        console.log(indexValue)
        messageList.splice(indexValue, 1)
        // setTodoList([...todoList])
    }

    const handelEdit = (getId) => {
        // const value = todoList.find((data)=>(data.id === getId))
        // setInputValue(value.message)
        // setGetId(getId)
    }

    return(
        <>
        {messageList?.length !== 0 ? (
            <ol>
                {
                   messageList?.map((value)=>(
                    <li key={value.id}>
                        <span>{value.message}</span>
                        <span>
                            <button onClick={()=>handelDelete(value.id)}>Delete</button>
                            {/* <button onClick={()=>handelEdit(value.id)}>Edit</button> */}
                        </span>
                    </li>
                   )) 
                }
            </ol>
            ) : (
                <h4>There is no data</h4>
            )}
        </>
    )
}

export default MessageList
