const UseEffect = () => {
  const [count, setCount] = useState(0)
  const [number, setnumber] = useState(0)

    useEffect(()=>{
        console.log("useEffect called");
    })

  return (
    <div>
      <h1>Use State { count } </h1>
      <button onClick={()=>setCount(count+1)}>Click me</button>
      <button onClick={()=>setCount(count+1)}>Click me</button>
    </div>
  )
}

export default UseEffect
