import React, { useState } from 'react'

const Form = () => {
    const [todo, setTodo] = useState({
        message: ""
    })
    const handleChange = (e) => {
        setTodo()
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        setTodo('')
        console.log(todo)
    }

    return(
        <>
            <div>
                {todo.map((data, index) => {
                    <span key="index">{data}</span>
                })}
            </div>
            <form onSubmit={handleSubmit}>
                <input type={"text"} placeholder={"Add a ToDo"} value={todo} onChange={handleChange}/>
                <button type={'submit'}>Submit</button>
            </form>
        </>
    )
}

export default Form
