import React from 'react'
import { Checkbox, FormControlLabel, FormHelperText, FormLabel, InputLabel, MenuItem, Radio, RadioGroup, InputAdornment, TextField } from '@mui/material'
// import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
// import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
// import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { FormControl } from '@mui/base'
import styles from './employeeForm.module.scss'

const EmployeeForm = (props) => {
    const {cities, skills, inputValue, handelOnChange} = props

  return (
    <form>
      <div className={styles['form']}>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='fullName' required>Full Name</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='text'
                id='fullName'
                // label="Full Name"
                placeholder='Enter Full Name'
                name="fullName"
                value={inputValue.fullName}
                onChange={(e)=>handelOnChange('fullName', e.target.value)}
                error={Boolean(!inputValue.fullName)}
                helperText={Boolean(!inputValue.fullName) && "This Field is required."}
                required
                />
            </FormControl>
            {/* {console.log(inputValue.fullName.length)} */}
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor="email" required>Email</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='email'
                id='email'
                // label="Email"
                placeholder='Enter Email Id'
                name="email"
                value={inputValue.email}
                onChange={(e)=>handelOnChange('email', e.target.value)}
                error={Boolean(!inputValue.email)}
                helperText={Boolean(!inputValue.email) && "This Field is required."}
                required
                />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='contactNumber' required>Contact Number</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='number'
                id='contactNumber'
                // label="Contact Number"
                placeholder='Enter contact number'
                name="contactNumber"
                value={inputValue.contact}
                onChange={(e)=>handelOnChange('contact', e.target.value)}
                error={Boolean(!inputValue.contact)}
                helperText={Boolean(!inputValue.contact) && "This Field is required."}
                required
                InputProps={{
                    startAdornment:<InputAdornment position='start'>+91</InputAdornment>
                }}
                />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='dateOfBirth' required>D.O.B</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='date'
                id='dateOfBirth'
                // label="Date of birth"
                name="dateOfBirth"
                value={inputValue.dob}
                onChange={(e)=>handelOnChange('dob', e.target.value)}
                error={Boolean(!inputValue.dob)}
                helperText={Boolean(!inputValue.dob) && "This Field is required."}
                required
                />
            </FormControl>
            {/* <InputLabel>D.O.B</InputLabel> */}
            {/* <LocalizationProvider dateAdapter={AdapterDayjs}>
                <DatePicker value={inputValue.dob} onChange={(e)=>handelOnChange('dob', e.$D)} />
            </LocalizationProvider> */}
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <InputLabel htmlFor="city" required>City</InputLabel>
                <TextField 
                className='textcapitalize'
                fullWidth
                select
                // label="Select City"
                id='city'
                name="city"
                value={inputValue.city} 
                onChange={(e)=>handelOnChange('city', e.target.value)}
                SelectProps={{ }}
                error={Boolean(!inputValue.city)}
                helperText={Boolean(!inputValue.city) && "This Field is required."}
                required
                defaultValue={"Select-City"}
                >
                    <MenuItem className='textcapitalize' value={"Select-City"}>Select City</MenuItem>
                    {cities.map((cityName, index) => (
                        <MenuItem className='textcapitalize' key={index} value={cityName}>{cityName}</MenuItem>
                    ))}
                </TextField>
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='skill' required>Skill</FormLabel>
                <TextField 
                className='textcapitalize'
                fullWidth
                select
                label="Select Skills"
                id='skill'
                name="skill"
                value={inputValue.skill} 
                onChange={(e)=>handelOnChange('skill', e.target.value)}
                SelectProps={{ multiple:true }} 
                error={Boolean(!inputValue.skill.length >= 1)}
                helperText={!Boolean(inputValue.skill.length >= 1) && "This Field is required."}
                required
                >
                    {skills.map((skill, index)=>(
                        <MenuItem className='textcapitalize' key={index} value={skill}>{skill}</MenuItem>
                    ))}
                </TextField>
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                {/* <FormLabel htmlFor='gender' required>Select Gender</FormLabel> */}
                <RadioGroup 
                row
                id='gender'
                name="gender"
                value={inputValue.gender}
                onChange={(e)=>handelOnChange('gender', e.target.value)}
                >
                    <FormControlLabel control={<Radio />} value="female" label="Female" />
                    <FormControlLabel control={<Radio />} value="male" label="Male" />
                    <FormControlLabel control={<Radio />} value="other" label="Other" />
                </RadioGroup>
                <FormHelperText error={Boolean(!inputValue.gender)}>This Field is required.</FormHelperText>
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormControlLabel
                control={<Checkbox />} 
                label="This is active user" 
                name="isActive"
                value={inputValue.isActive}
                onChange={(value)=>handelOnChange('isActive', value.target.checked)}
                />
                <FormHelperText error={Boolean(!inputValue.isActive)}>This Field is required.</FormHelperText>
            </FormControl>
        </div>
      </div>
    </form>
  )
}

export default EmployeeForm
