import React from "react";
import { useDynamicSvgImport } from "../Hooks/useDynamicSvgImport";


interface IProps {
  name: string;
  customClass?: string;
  svgProp?: React.SVGProps<SVGSVGElement>;
  height?: string;
  width?: string;
  color?: string;
}

const transparentIcons = ['workIcon'];

function Icon(props: IProps) {
  const { name, customClass, svgProp, width, height, color } = props;
  const { loading, SvgIcon } = useDynamicSvgImport(name);

  return (
    <>
      {loading && <div className="rounded-full bg-slate-400 animate-pulse h-8 w-8" />}
      {SvgIcon && (
        <div className={customClass}>
          {transparentIcons.includes(name) ? (
            <SvgIcon {...svgProp} width={width} height={height} fill={color} fill-opacity="0" />
          ) : (
            <SvgIcon {...svgProp} width={width} height={height} fill={color} />
          )}
        </div>
      )}
    </>
  );
}

export default Icon;
