import React, { useState } from 'react';
import shortid from 'shortid'

const Todo = () => {
    const [inputValue, setInputValue] = useState('');
    const [todoList, setTodoList] = useState([]);

    const handelOnChange = (event) => {
        setInputValue(event.target.value)
        // console.log(inputValue)
    }

    const handelOnClick = (event) => {
        event.preventDefault()
        setInputValue('')
        setTodoList([...todoList, {id:shortid.generate(), message: inputValue}])
        // console.log(todoList)
    }

    const handelDelete = (uid) => {
        const indexValue = todoList.find((data)=>(data.id === uid))
        console.log(indexValue)
    }

    return(
        <>
            <h1>Todo List</h1>
            <form>
                <input 
                type={'text'} 
                value={inputValue} 
                placeholder={'Add todo'} 
                onChange={handelOnChange}
                />
                <button onClick={handelOnClick}>Add todo</button>
            </form>


            <ul>
                {
                   todoList?.map((value, index)=>(
                    <li key={value.id}>
                        <span>{index+1}. {value.message}</span>
                        <span>
                            <button onClick={()=>handelDelete(value.id)}>Delete</button>
                            <button>Edit</button>
                        </span>
                    </li>
                   )) 
                }
            </ul>
        </>
    )

}

export default Todo
