import React, { useRef, useState } from 'react'
import { Avatar, Button, FormHelperText } from '@mui/material';
import styles from './UploadImage.module.scss'
import avatar from '../../assets/dummy-product.png'


const UploadImage = (props) => {
  const {initialValues, errors, handleChange} = props;
  const inputRef = useRef(null);
  const [image, setImage] = useState(initialValues.profileImg);

  const handleImgClick = () => {
    inputRef.current.click();
  }
  const handleImageUpload = (e) => {
    // console.log(e.target.files[0])
    setImage(e.target.files[0])
  }

  return (
    <>
      <div className={styles.avatarBx} style={{alignItems: 'center', justifyContent: 'flex-start', display: 'flex'}} onClick={handleImgClick}>
        <Avatar alt="dummy" src={image ? URL.createObjectURL(image) : avatar} className={styles['avatarsize']} />
        <Button autoFocus variant="outlined">
          Upload Image
        </Button>
        <input 
        type="file" 
        ref={inputRef} 
        hidden
        id='profileImg'
        name="profileImg"
        required
        value={initialValues.profileImg}
        onChange={handleChange} 
        />
      </div>
      {errors.profileImg && <FormHelperText className='Mui-error'>{errors.profileImg}</FormHelperText>}
    </>
  )
}

export default UploadImage
