import React, { useMemo, useState } from 'react';

const UseMemo = () => {
  const [add, setAdd] = useState(1);

  const dataVal = useMemo(
    function addition(a) {
        setAdd(add + a);
        console.log(">>>>>>>>>>>>>>>>>>>>>>>>>>>>")
    }
  )



  return (
    <div>
      <h1>Use Memo</h1>
      <button onClick={()=>dataVal(5)}>Increment {add}</button>
    </div>
  );
};

export default UseMemo;
