import React, { useState } from 'react'
import { Button } from '@mui/material'
import { DialogBox } from '../genericComponent/DialogBox'
import EmployeeForm from './EmployeeForm'
import shortid from 'shortid';

const Employee = () => {
  const cities = ['delhi', 'mumbai', 'banglore', 'kolkata', 'pune'];
  const skills = ['angular', 'react', 'vue', 'nextjs', 'drupal', 'html5', 'css3', 'sass', 'mui', 'material-ui'];

  const [addEmp, setAddEmp] = useState(false)
  const [error, setError] = useState({})
  const addEmployeeBtn = () => { setAddEmp(!addEmp) }

  const [inputValue, setInputValue] = useState({
    fullName: "",
    email: "",
    contact: "",
    dob: "",
    city: "",
    skill: [],
    gender: "",
    errors: {}
  })

  const handelOnChange = (key, value) => {
    setInputValue((prevValue) => ({...prevValue, [key]: value}));
  }

  const handelSubmit = (event) => {
    // console.log("ðŸš€ ~ file: index.js:31 ~ handelSubmit ~ event:", event)
    const { fullName, email, contact, dob, city, skill, gender } = inputValue;
    if(fullName === '') {
      setError({ ...error, fullName: "This Field is required." })
    }
    else if(fullName.length <= 2){
      setError({ ...error, fullName: "Please enter minimum 3 charector" })
    }




    console.log(fullName)
    // Object.keys(inputValue).forEach(key => {
    //   if(inputValue[key]){
    //     console.log("Avalable")
    //   }else {
    //     console.log("Not Avalable")
    //     setError(true)
    //   }
    // })
    // console.log(inputValue.fullName.length)
    // console.log({id:shortid.generate(), ...inputValue})
    // console.log(inputValue)
  }

  return (
    <>
      <div className='d-flex align-items-center justify-content-between'>
        <h1>Employee List</h1>
        <Button variant="contained" onClick={()=>addEmployeeBtn()}>Add Employee</Button>
        <DialogBox
          maxWidth="md" 
          title={'Add Employee'} 
          openModal={addEmp} 
          closeModel={addEmployeeBtn} 
          resetForm={inputValue}
          handelSubmit={handelSubmit}
          modelData={
            <EmployeeForm 
              cities={cities}
              skills={skills}
              inputValue={inputValue}
              error={error}
              handelOnChange={handelOnChange} 
            />
          } 
        />
      </div>
      <ul>
      {/* {
        allData.map((data) => (
          <li key={data.id}>{data.fullName}</li>
        ))
      } */}

      </ul>
    </>
  )
}

export default Employee
