import React, { useRef, useState } from 'react'
import { Checkbox, FormControlLabel, FormHelperText, FormLabel, MenuItem, Radio, RadioGroup, InputAdornment, TextField, Avatar, Button } from '@mui/material'
import { FormControl } from '@mui/base'
import styles from './employeeForm.module.scss'
import avatar from '../../assets/dummy-product.png'


const EmployeeForm = (props) => {
  const {cities, skills, initialValues, handleChange, errors, profileImage} = props;

  const inputRef = useRef(null);

  const handleImgClick = () => {
    inputRef.current.click();
  }

  // const [image, setImage] = useState();
  // const handleImageChange = (e) => {
  //   console.log(e.target.files[0])
  //   setImage(e.target.files[0])
  // }

  return (
    <form>
      <div className={styles['form']}>

        <div className={styles.formRow}>
          <div className={styles.avatarBx} style={{alignItems: 'center', justifyContent: 'flex-start', display: 'flex'}} onClick={handleImgClick}>
            <Avatar alt="dummy" src={profileImage ? URL.createObjectURL(profileImage) : avatar} className={styles['avatarsize']} />
            <Button autoFocus variant="outlined">
              Upload Image
            </Button>
            <input 
            type="file" 
            ref={inputRef} 
            hidden
            id='profileImg'
            name="profileImg"
            required
            value={initialValues.profileImg}
            onChange={handleImageChange} 
            />
          </div>
          {errors.profileImg && <FormHelperText className='Mui-error'>{errors.profileImg}</FormHelperText>}
        </div>

        <div className={styles.formRow}>
          <FormControl>
            <FormLabel htmlFor='fullName' required>Full Name</FormLabel>
            <TextField
            fullWidth
            variant='outlined'
            type='text'
            autoComplete='off'
            id='fullName'
            name="fullName"
            placeholder='Enter Full Name'
            required
            error={Boolean(errors.fullName)}
            helperText={errors.fullName}            
            value={initialValues.fullName}
            onChange={handleChange}
            />
          </FormControl>
        </div>

        <div className={styles.formRow}>
          <FormControl>
            <FormLabel htmlFor="email" required>Email</FormLabel>
            <TextField
            fullWidth
            variant='outlined'
            type='email'
            autoComplete='off'
            id='email'
            name="email"
            placeholder='Enter Email Id'
            required
            error={Boolean(errors.email)}
            helperText={errors.email}
            value={initialValues.email}
            onChange={handleChange}
            />
          </FormControl>
        </div>

        <div className={styles.formRow}>
          <FormControl>
            <FormLabel htmlFor="contactNumber" required>Contact Number</FormLabel>
            <TextField
            fullWidth
            variant='outlined'
            type='tel'
            autoComplete='off'
            id='contactNumber'
            name="contactNumber"
            placeholder='Enter Contact Number'
            required
            InputProps={{
              startAdornment:<InputAdornment position='start'>+91</InputAdornment>
            }}
            error={Boolean(errors.contactNumber)}
            helperText={errors.contactNumber}
            value={initialValues.contactNumber}
            onChange={handleChange}
            inputProps={{
              maxLength: 10,
            }}
            />
          </FormControl>
        </div>

        <div className={styles.formRow}>
          <FormControl>
            <FormLabel htmlFor="dateOfBirth" required>Date Of Birth</FormLabel>
            <TextField
            fullWidth
            variant='outlined'
            type='date'
            autoComplete='off'
            id='dateOfBirth'
            name="dateOfBirth"
            // placeholder='Enter Contact Number'
            required
            error={Boolean(errors.dateOfBirth)}
            helperText={errors.dateOfBirth}
            value={initialValues.dateOfBirth}
            onChange={handleChange}
            />
          </FormControl>
        </div>

        <div className={styles.formRow}>
          <FormControl>
            <FormLabel htmlFor="city" required>City</FormLabel>
            <TextField
            fullWidth
            variant='outlined'
            select
            autoComplete='off'
            id='city'
            name="city"
            required
            error={Boolean(errors.city)}
            helperText={errors.city}
            value={initialValues.city}
            onChange={handleChange}
            >
              {/* <MenuItem value={"select-city"}>Select City</MenuItem> */}
              {cities.map((cityName, index) => (
                  <MenuItem key={index} value={cityName}>{cityName}</MenuItem>
              ))}
            </TextField>
          </FormControl>
        </div>

        <div className={styles.formRow}>
          <FormControl>
            <FormLabel htmlFor="skill" required>Skill</FormLabel>
            <TextField
            fullWidth
            variant='outlined'
            select
            multiple
            SelectProps={{multiple: true}}
            autoComplete='off'
            id='skill'
            name="skill"
            required
            error={Boolean(errors.skill)}
            helperText={errors.skill}
            value={initialValues.skill}
            onChange={handleChange}
            >
              {skills.map((skillVal, index) => (
                  <MenuItem key={index} value={skillVal}>{skillVal}</MenuItem>
              ))}
            </TextField>
          </FormControl>
        </div>

        <div className={styles.formRow}>
          <FormControl>
            <FormLabel htmlFor='message' required>Message</FormLabel>
            <TextField
            fullWidth
            variant='outlined'
            type='textarea'
            multiline
            rows={4}
            id='message'
            name="message"
            placeholder='Type here comments'
            required
            error={Boolean(errors.message)}
            helperText={errors.message}            
            value={initialValues.message}
            onChange={handleChange}
            inputProps={{
              maxLength: 250,
            }}
            />
            <FormHelperText>Maximum {250 - (initialValues.message.length)} latter</FormHelperText>
          </FormControl>
        </div>

        <div className={styles.formRow}>
          <FormControl>
            <FormLabel htmlFor='gender' required>Select Gender</FormLabel>
            <RadioGroup 
            row
            id='gender'
            name="gender"
            value={initialValues.gender}
            onChange={handleChange}>
              <FormControlLabel control={<Radio />} value="female" label="Female" />
              <FormControlLabel control={<Radio />} value="male" label="Male" />
              <FormControlLabel control={<Radio />} value="other" label="Other" />
            </RadioGroup>
            <FormHelperText className='Mui-error'>{errors.gender}</FormHelperText>
          </FormControl>
        {/* </div>

        <div className={styles.formRow}> */}
          <FormControl>
            <FormLabel htmlFor='gender' required>Employee Status</FormLabel><br />
            <FormControlLabel
            control={<Checkbox />} 
            label="This is active user" 
            id='isActive'
            name="isActive"
            value={initialValues}
            onChange={handleChange}
            />
          </FormControl>
          <FormHelperText className='Mui-error'>{errors.isActive}</FormHelperText>
        </div>

      </div>
    </form>
  )
}

export default EmployeeForm
