import React, {useState} from 'react';
import shortid from 'shortid';

export function Form() {
    const [todo, setTodo] = useState('');
    const [todoList, setTodoList] = useState([]);

    let idFlag;

    const handelChange = (e) => {
        setTodo(e.target.value)
    }

    const handelSubmit = (e) => {
        e.preventDefault()
        // if(idFlag){
            setTodoList([...todoList, {id:shortid.generate(), name: todo}])
            setTodo('')
        // }else {
        //     console.log("i have id")
        // }
    }

    const handelDelete = (id) => {
        const newTodoList = todoList.findIndex((item)=>item.id === id);
        console.log(newTodoList)
        // todoList.splice(newTodoList, 1); 
        // setTodoList(todoList);

        // console.log(todoList)
        // console.log(newTodoList)
        // newTodoList.splice(newTodoList[index].id, 1); 
    }

    const handelEdit = (data) => {
        console.log(data)
        setTodo(data.name)
        idFlag = data.id
    }

    return(
        <>
        <form onSubmit={handelSubmit}>
            <input 
                type={'text'}
                placeholder={'Add todo'}
                value={todo}
                onChange={handelChange}
            />
            <button type={'submit'}>Add Todo</button>
        </form>
        <ul>
            {todoList?.map((item, index)=>(
                <li key={item.id}>
                    <span>{item.name}</span>
                    <span>
                        <button onClick={()=>handelDelete(item.id)} type={'button'}>Delete</button>
                        <button onClick={()=>handelEdit(item)}>Edit</button>
                    </span>
                </li>
            ))}
        </ul>
        </>
    )
}



