import React from 'react';
import Avatar from '@mui/joy/Avatar';
import Chip from '@mui/joy/Chip';
import Button from '@mui/joy/Button';
import ButtonGroup from '@mui/joy/ButtonGroup';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import CardOverflow from '@mui/joy/CardOverflow';
import CardActions from '@mui/joy/CardActions';
import Typography from '@mui/joy/Typography';
import { DialogBox } from '../DialogBox';

const CustomLink = (props) => <a {...props}>{props.children}</a>;

const ProfileCrd = (props) => {
  const { data } = props;
  const {firstName, lastName, maidenName, email, phone, image} = data;

  return (
    <>
      <Card>
        <CardContent sx={{ alignItems: 'center', textAlign: 'center' }}>
          {props.children}
          <Avatar src="{image}" sx={{ '--Avatar-size': '4rem' }} />
          <Chip
            size="sm"
            variant="soft"
            color={true ? 'primary' : 'secondary'}
            sx={{
              mt: -1,
              mb: 1,
              border: '3px solid',
              borderColor: 'background.surface',
            }}
          >
            {true ? 'PRO' : 'Normal'}
          </Chip>
          <Typography level="title-lg">{firstName} {maidenName} {lastName}</Typography>
          <Typography level="body-sm" sx={{ maxWidth: '100%' }}>
            Hello
          </Typography>
        </CardContent>
        <CardOverflow sx={{ bgcolor: 'background.level1' }}>
          <CardActions buttonFlex="1">
            <ButtonGroup variant="outlined" sx={{ bgcolor: 'background.surface' }}>
            <Button href={"tel:" + contactNumber} component={CustomLink} variant="outlined">Connect</Button>
              <Button variant="outlined">More Detail</Button>
            </ButtonGroup>
          </CardActions>
        </CardOverflow>
      </Card>
    </>
  )
}

export default ProfileCrd
