import { Button, Checkbox, FormControlLabel, FormHelperText, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'
import axios from 'axios';

const Login = () => {

  const formInitialVal = { email: '', password: '' }
  const [initialValue, setInitialValue] = useState(formInitialVal);
  const [showError, setShowError] = useState(false);
  const [formErrors, setFormErrors] = useState({});


  const validateMsg = (values) => {
    const errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

    if (!values.email) {
      errors.email = "This filed is required."
    } else if(!emailRegex.test(values.email)) {
      setShowError(true)
    } else if (emailRegex.test(values.email)) {
      setShowError(false)
    }

    if (!values.password) {
      errors.password = "This filed is required."
    } else if(values.password.length < 8) {
      setShowError(true)
    } else if(!passwordRegex.test(values.password)) {
      setShowError(true)
    }else if (passwordRegex.test(values.password)) {
      setShowError(false)
    }

    return errors;
  }

  const handleChange = (e) => {
    const {name, value} = e.target;
    setInitialValue({ ...initialValue, [name]: value })
  }

  const handleSubmit = () => {
    const errors = validateMsg(initialValue)
    setFormErrors(errors)
    console.log(errors)
    if (Object.keys(errors)?.length) {
      return false
    }


    // axios.post(`${'http://localhost:3100/api/login'}`, initialValue)
    // .then(res => {
    //   console.log("ðŸš€ ~ file: index.js:18 ~ handleSubmit ~ res:", res)

    // })
    // .catch(err => console.log(err))
  }

  return (
    <form style={{width: '100%'}}>
        <Typography component="h1" variant="h5">
        Sign In
        </Typography>
        <TextField
        margin="normal"
        required
        fullWidth
        id="email"
        label="Email Address"
        name="email"
        autoComplete="email"
        autoFocus
        error={Boolean(formErrors.email)}
        helperText={formErrors.email}
        value={initialValue.email}
        onChange={handleChange}
        />
        <TextField
        margin="normal"
        required
        fullWidth
        name="password"
        label="Password"
        type="password"
        id="password"
        autoComplete="current-password"
        error={Boolean(formErrors.password)}
        helperText={formErrors.password}
        value={initialValue.password}
        onChange={handleChange}
        />
        {showError && <FormHelperText className='Mui-error'>Email or Password wrong!</FormHelperText>}
        {/* <FormControlLabel
        control={<Checkbox value="remember" color="primary" />}
        label="Remember me"
        /> */}
        <Button
        type="button"
        fullWidth
        variant="contained"
        sx={{ mt: 3, mb: 2 }}
        onClick={handleSubmit}
        >
        Sign In
        </Button>
    </form>
  )
}

export default Login
