import React, { useMemo, useState } from 'react';

const UseMemo = () => {
  const [add, setAdd] = useState(0);

  const handleClick = useMemo(()=>{
    return (a,b) => {
        console.log(a+b)
        setAdd(a+b)
    }
  }, [add])


  return (
    <div>
      <h1>Use Memo</h1>
      <button onClick={() => handleClick(5,5)}>Increment {add}</button>
    </div>
  );
};

export default UseMemo;
