import React from 'react'
import styles from './Header.module.scss'
import logo from '../../assets/react.svg'
import profileImg from '../../assets/doctor.jpg'
import { Avatar, Box, Button, IconButton, Menu } from '@mui/material'
import { MenuItem } from '@mui/base'

const Header = () => {
  let isLogedIn = true;
  return (
    <header className={styles['header-top']}>
      <div className='container d-flex flex-nowrap align-items-center'>
        <span className={styles['company-logo']}>
          <img src={logo} alt="logo" width="50" height="50" />
        </span>
        <span className='flex-1'></span>
        <div className='d-flex flex-nowrap align-items-center gap-8'>
          {isLogedIn
            ? <>
                <Avatar alt="Remy Sharp" src={profileImg} />
                <Button variant="text">Riya Sinha</Button>
              </>
            : <Button variant="outlined">Login</Button>
          }
        </div>

        <Box sx={{ display: 'flex', alignItems: 'center', textAlign: 'center' }}>
          <IconButton
              // onClick={handleClick}
              size="small"
              sx={{ ml: 2 }}
              // aria-controls={open ? 'account-menu' : undefined}
              aria-haspopup="true"
              // aria-expanded={open ? 'true' : undefined}
            >
              <Avatar sx={{ width: 32, height: 32 }}>M</Avatar>
          </IconButton>
        </Box>
        <Menu
        // anchorEl={anchorEl}
        id="account-menu"
        // open={open}
        // onClose={handleClose}
        // onClick={handleClose}
        PaperProps={{
          elevation: 0,
          sx: {
            overflow: 'visible',
            filter: 'drop-shadow(0px 2px 8px rgba(0,0,0,0.32))',
            mt: 1.5,
            '& .MuiAvatar-root': {
              width: 32,
              height: 32,
              ml: -0.5,
              mr: 1,
            },
            '&:before': {
              content: '""',
              display: 'block',
              position: 'absolute',
              top: 0,
              right: 14,
              width: 10,
              height: 10,
              bgcolor: 'background.paper',
              transform: 'translateY(-50%) rotate(45deg)',
              zIndex: 0,
            },
          },
        }}
        transformOrigin={{ horizontal: 'right', vertical: 'top' }}
        anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
      >
        <MenuItem 
        onClick={handleClose}>
          <Avatar /> Profile
        </MenuItem>
        <MenuItem onClick={handleClose}>
          <Avatar /> My account
        </MenuItem>
        <Divider />
        <MenuItem onClick={handleClose}>
          <ListItemIcon>
            <PersonAdd fontSize="small" />
          </ListItemIcon>
          Add another account
        </MenuItem>
        <MenuItem onClick={handleClose}>
          <ListItemIcon>
            <Settings fontSize="small" />
          </ListItemIcon>
          Settings
        </MenuItem>
        <MenuItem onClick={handleClose}>
          <ListItemIcon>
            <Logout fontSize="small" />
          </ListItemIcon>
          Logout
        </MenuItem>
      </Menu>

      </div>
    </header>
  )
}

export default Header
