import React, {useState} from 'react';

const Counter = () => {
    const [count, setCount] = useState(0);

    const increment = () => {
        if(count < 10){
            setCount(count + 1)
        }
    }

    const decrement = () => {
        if(count > 0){
            setCount(count - 1)
        }
    }

    return(
        <>
        <h1>Counter is: {count}</h1>
        <button onClick={increment}>Increment</button>
        <button onClick={decrement}>Decrement</button>
        </>
    )
}

export default Counter
