import React from 'react'
import { Checkbox, FormControlLabel, FormHelperText, FormLabel, InputLabel, MenuItem, Radio, RadioGroup, InputAdornment, TextField } from '@mui/material'
import { FormControl } from '@mui/base'
import styles from './employeeForm.module.scss'

const EmployeeForm = (props) => {
    const {cities, skills, values, handleChange} = props

  return (
    <form>
      <div className={styles['form']}>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='fullName' required>Full Name</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='text'
                autoComplete='off'
                id='fullName'
                placeholder='Enter Full Name'
                name="fullName"
                value={values.fullName}
                onChange={(e) => handleChange('fullName', e.target.value)}
                // label="Full Name"
                // onChange={handleChange}
                // error={errors}
                // helperText={errors && "This Field is required."}               
                />
            </FormControl>
        </div>

        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor="email" required>Email</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='email'
                id='email'
                placeholder='Enter Email Id'
                name="email"
                value={values.email}
                onChange={(e) => handleChange('email', e.target.value)}
                // label="Email"
                // onChange={handleChange}
                // error={errors}
                // helperText={errors && "This Field is required."}
                />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='contactNumber' required>Contact Number</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='number'
                id='contactNumber'
                placeholder='Enter contact number'
                name="contactNumber"
                value={values.contact}
                onChange={(e) => handleChange('contact', e.target.value)}
                // label="Contact Number"
                // onChange={handleChange}
                // error={errors}
                // helperText={errors && "This Field is required."}
                InputProps={{
                    startAdornment:<InputAdornment position='start'>+91</InputAdornment>
                }}
                />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='dateOfBirth' required>D.O.B</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='date'
                id='dateOfBirth'
                name="dateOfBirth"
                value={values.dob}
                onChange={(e) => handleChange('dob', e.target.value)}
                // label="Date of birth"
                // onChange={handleChange}
                // error={errors}
                // helperText={errors && "This Field is required."}
                />
            </FormControl>
            {/* <InputLabel>D.O.B</InputLabel> */}
            {/* <LocalizationProvider dateAdapter={AdapterDayjs}>
                <DatePicker value={values.dob} onChange={(e)=>handelOnChange('dob', e.$D)} />
            </LocalizationProvider> */}
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <InputLabel htmlFor="city" required>City</InputLabel>
                <TextField 
                className='textcapitalize'
                fullWidth
                select
                id='city'
                name="city"
                label="Select City"
                value={values.city} 
                onChange={(e) => handleChange('city', e.target.value)}
                // onChange={handleChange}
                // error={errors}
                // helperText={errors && "This Field is required."}
                // SelectProps={{ }}
                // defaultValue={"Select-City"}
                >
                    <MenuItem className='textcapitalize' value={"Select-City"}>Select City</MenuItem>
                    {cities.map((cityName, index) => (
                        <MenuItem className='textcapitalize' key={index} value={cityName}>{cityName}</MenuItem>
                    ))}
                </TextField>
            </FormControl>
        </div>

        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='skill' required>Skill</FormLabel>
                <TextField 
                className='textcapitalize'
                fullWidth
                select
                label="Select Skills"
                id='skill'
                name="skill"
                SelectProps={{ multiple:true }}
                value={values.skill} 
                onChange={(e) => handleChange('skill', e.target.value)}
                // onChange={handleChange}
                // error={errors?.skill}
                // helperText={errors?.skill && "This Field is required."}
                >
                    {skills.map((skill, index)=>(
                        <MenuItem className='textcapitalize' key={index} value={skill}>{skill}</MenuItem>
                    ))}
                </TextField>
            </FormControl>
        </div>


        <div className={styles.formRow}>
            <FormControl>
                {/* <FormLabel htmlFor='gender' required>Select Gender</FormLabel> */}
                <RadioGroup 
                row
                id='gender'
                name="gender"
                value={values.gender}
                onChange={(e) => handleChange('gender', e.target.value)}
                // onChange={handleChange}
                >
                    <FormControlLabel control={<Radio />} value="female" label="Female" />
                    <FormControlLabel control={<Radio />} value="male" label="Male" />
                    <FormControlLabel control={<Radio />} value="other" label="Other" />
                </RadioGroup>
                {/* { errors.gender && <FormHelperText error={errors}>This Field is required.</FormHelperText> } */}
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormControlLabel
                control={<Checkbox />} 
                label="This is active user" 
                name="isActive"
                // value={values.isActive}
                // onChange={handleChange}
                />
                {/* { errors.isActive && <FormHelperText error={errors}>This Field is required.</FormHelperText> } */}
            </FormControl>
        </div>
      </div>
    </form>
  )
}

export default EmployeeForm





















