import { Button, Checkbox, FormControlLabel, FormHelperText, Grid, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const Login = () => {

  const formInitialVal = { email: '', password: '' }
  const [initialValue, setInitialValue] = useState(formInitialVal);
  const [showError, setShowError] = useState(false);
  const [formErrors, setFormErrors] = useState({});

  const navigate=useNavigate();

  const validateMsg = (values) => {
    const errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;

    if (!values.email) {
      errors.email = "This filed is required."
    } else if(!emailRegex.test(values.email)) {
      errors.email = "This is not a valid email."
    }

    if (!values.password) {
      errors.password = "This filed is required."
    } else if(values.password.length < 8) {
      errors.password = "Password should be minimum 8 characters."
    } else if(!passwordRegex.test(values.password)) {
      errors.password = "Password format should be Abcd@123"
    }

    return errors;
  }

  const handleChange = (e) => {
    const {name, value} = e.target;
    setInitialValue({ ...initialValue, [name]: value })
  }

  const handleSubmit = async () => {
    const errors = validateMsg(initialValue)
    setFormErrors(errors)
    console.log(errors)
    if (Object.keys(errors)?.length) {
      return false
    }
    console.log(initialValue)
    axios.post(`${'http://localhost:3100/api/login'}`, initialValue)
    .then(res => {
      localStorage.setItem("access_token", res.data.token);
      if(res?.status===200) {
        navigate('/dashboard');
      }  
    })
    .catch(err => console.log(err))

  }

  return (
    <form style={{width: '100%'}}>
        <Typography component="h1" variant="h5">
        Sign In
        </Typography>
        <TextField
        margin="normal"
        required
        fullWidth
        id="email"
        label="Email Address"
        name="email"
        autoComplete="email"
        autoFocus
        error={Boolean(formErrors.email)}
        helperText={formErrors.email}
        value={initialValue.email}
        onChange={handleChange}
        />
        <TextField
        margin="normal"
        required
        fullWidth
        name="password"
        label="Password"
        type="password"
        id="password"
        autoComplete="current-password"
        error={Boolean(formErrors.password)}
        helperText={formErrors.password}
        value={initialValue.password}
        onChange={handleChange}
        />
        {showError && <FormHelperText className='Mui-error'>Email or Password wrong!</FormHelperText>}
        {/* <FormControlLabel
        control={<Checkbox value="remember" color="primary" />}
        label="Remember me"
        /> */}
        <Button
        type="button"
        fullWidth
        variant="contained"
        sx={{ mt: 3, mb: 2 }}
        onClick={handleSubmit}
        >
        Sign In
        </Button>
        <Grid container>
          <Grid item xs>
            <Link href="javascript:void(0)" variant="body2">Forgot password?</Link>
          </Grid>
          <Grid item>
            Don't have an account? <Link href="javascript:void(0)" variant="body2">Sign Up</Link>
          </Grid>
        </Grid>
    </form>
  )
}

export default Login









