import React, {useState} from 'react';
import shortid from 'shortid';
o

export function Form() {
    const [todo, setTodo] = useState('');
    const [todoList, setTodoList] = useState([]);
    const [editID, setEditID] = useState(null);
    const [validate, setValidate] = useState(false);
    const [buttonFlag, setButtonFlag] = useState(true);
    const [charectorCount, setCharectorCount] = useState(null);

    const danger = {
        color: "red"
    }

    const handelChange = (e)=> {
        setTodo(e.target.value)
        if(e.target.value.length > 2) {
            setValidate(false)
        }else if(e.target.value.length < 3){
            setValidate(true)
        }
    }

    const handelSubmit = (e) => {
        e.preventDefault()
        if(!todo){
            setValidate(true)
        }
        else if(todo && !buttonFlag && !validate){
            setTodoList(
                todoList.map((data)=>{
                    if(data.id === editID){
                        return {...data, name:todo}
                    }
                    return data
                })
            )
            setButtonFlag(true)
            setTodo('')
            setValidate(false)
        }
        else {
            if(!validate){
                setTodoList([...todoList, {id:shortid.generate(), name: todo}])
                setTodo('')
                setValidate(false)
                console.log(todoList)
            }
        }
    }

    const handelDelete = (id) => {
        const indexValue = todoList.findIndex((item)=>item.id === id);
        todoList.splice(indexValue, 1); 
        setTodoList([...todoList]);
    }

    const handelEdit = (value) => {
        setButtonFlag(false)
        setTodo(value.name)
        setEditID(value.id)
        setValidate(false)
    }

    return(
        <>
            <form>
                <input 
                    type={'text'}
                    placeholder={'Add todo'}
                    value={todo}
                    onChange={handelChange}
                />
                <button onClick={handelSubmit} type={'submit'}>{buttonFlag ? 'Add' : 'Edit'} Todo</button>
                <br />
                {validate && <small style={danger}>Please Enter Value</small>}
            </form>
            {
                todoList?.length ? 
                <ul className={`${'hello'} `}>
                    {todoList?.map((item, index)=>(
                        <li key={item.id}>
                            <span>{item.name}</span>
                            <span>
                                <button onClick={()=>handelDelete(item.id)} type={'button'}>Delete</button>
                                <button onClick={()=>handelEdit(item)}>Edit</button>
                            </span>
                        </li>
                    ))}
                </ul> : <h4>There is no data avalable</h4>
            }

        </>
    )
}
