import React, { useState, useEffect } from 'react'
import axios from 'axios';
import { Button } from '@mui/material'
import { DialogBox } from '../genericComponent/DialogBox'
import EmployeeForm from './EmployeeForm'
import EmployeeList from './EmployeeList';

const Employee = () => {

  const dataVal = {
    profileImg: 'https://i.pravatar.cc/300',
    fullName: '', 
    email: '', 
    contactNumber: '', 
    dateOfBirth: '', 
    city: '',
    skill: [],
    gender: '',
    message: '',
    isActive: '',
  }

  const [popupModel, setPopupModel] = useState(false)
  const [deleteModelPop, setDeleteModelPop] = useState(false)
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit ] = useState(false);
  const [initialValues, setInitialValues] = useState(dataVal);

  const title = initialValues?.fullName;

  const API_LINK = "http://localhost:3000";
  const [empData, setEmpData] = useState([]);
  const [cities, setCities] = useState([]);
  const [skills, setSkills] = useState([]);
  const [pageLoad, setPageLoad] = useState(false)

  useEffect(()=> {
    axios.get(`${API_LINK}/employeeData`)
    .then(res => {
      setTimeout(() => {
        setEmpData(res.data)
      }, 3000);
    })
    .catch(err => console.log(err))

    axios.get(`${API_LINK}/cities`)
    .then(res => setCities(res.data))
    .catch(err => console.log(err))

    axios.get(`${API_LINK}/skills`)
    .then(res => setSkills(res.data))
    .catch(err => console.log(err))
  }, [pageLoad])

  const addEmployeeBtn = () => {
    setPopupModel(!popupModel)
    setInitialValues(dataVal)
    setPageLoad(false)
  }

  const handleChange = (e) => {
    const {name, value, checked, files} = e.target;     
    // if (name === "profileImg") {
    //   console.log(URL.createObjectURL(files[0]))
    //   setInitialValues({...initialValues, 'profileImg': files[0]})
    // }
    // else
    if(name === "isActive"){
      setInitialValues({ ...initialValues, [name]: checked })
    }
    else {
      setInitialValues({ ...initialValues, [name]: value })
    }
  }

  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

    // if (!values.profileImg) {
    //   errors.profileImg = "This field is required!"
    // }

    if (!values.fullName) {
      errors.fullName = "This field is required!"
    } else if (values.fullName.length < 3) {
      errors.fullName = "Name should be minimum 3 character."
    }

    if (!values.email) {
      errors.email = "This field is required!"
    }else if (!regex.test(values.email)){
      errors.email = "This is not a valid email."
    }

    if (!values.contactNumber) {
      errors.contactNumber = "This field is required!"
    } else if (values.contactNumber.length < 10){
      errors.contactNumber = "Contact number minimum 10 digits."
    } else if (values.contactNumber.length > 10){
      errors.contactNumber = "Contact number maximum 10 digits."
    }

    if (!values.dateOfBirth) {
      errors.dateOfBirth = "This field is required!"
    }

    if (!values.city) {
      errors.city = "This field is required!"
    }

    if (!values?.skill?.length) {
      errors.skill = "This field is required!"
    }

    if (!values.message) {
      errors.message = "This field is required!"
    } else if (values.message.length < 10){
      errors.message = "Message length minimum 10 letters"
    } else if (values.message.length > 250){
      errors.message = "Message length should be maximum 250 letters."
    }

    if (!values.gender) {
      errors.gender = "This field is required!"
    }

    // if (!values.isActive) {
    //   errors.isActive = "This field is required!"
    // }

    return errors;
  }

  const deleteModel = (val) => {
    setDeleteModelPop(!deleteModelPop)
    setInitialValues(val)
    setPageLoad(false)
  }

  const handleDelete = () => {
    console.log(initialValues.id)
    axios.delete(`${API_LINK}/employeeData/${initialValues.id}`)
    .then(res => {
      console.log("ðŸš€ ~ file: index.js:149 ~ handleDelete ~ res:", res)
      console.log("This emp deleted Successfully!")
    })
    .catch(err => console.log(err))
    setDeleteModelPop(false)
    setPageLoad(true)
  }

  const handelEdit = (val) => {
    console.log(val)
    setInitialValues(val)
    setPopupModel(true)
    setPageLoad(false)
  }

  const handleSubmit = () => {
    const errors = validate(initialValues)
    setFormErrors(errors)
    setIsSubmit(errors)
    console.log(errors)
    if (Object.keys(errors)?.length) {
      return false
    }
    else if (!initialValues.id) {
      axios.post(`${API_LINK}/employeeData`, initialValues)
      .then(res => {
        console.log("ðŸš€ ~ file: index.js:176 ~ handleSubmit ~ res:", res)
        console.log("Data added Successfully!")
      })
      .catch(err => console.log(err))
    }
    else {
      axios.put(`${API_LINK}/employeeData/${initialValues.id}`, initialValues)
      .then(res => {
        console.log("ðŸš€ ~ file: index.js:184 ~ handleSubmit ~ res:", res)
        console.log("Data update Successfully!")
      })
      .catch(err => console.log(err))
    }
    setInitialValues(dataVal)
    setFormErrors({})
    setIsSubmit(false)
    setPopupModel(false)
    setPageLoad(true)
  }

  useEffect(() => {
    if(Object.keys(formErrors).length === 0 && isSubmit){
      setIsSubmit(false)
    }
    if (isSubmit) {
      const errors = validate(initialValues)
      setFormErrors(errors)      
    }
  }, [formErrors, isSubmit])


  return (
    <main>
      <div className='container'>
        <div className='d-flex align-items-center justify-content-between'>
          <h1>Employee List </h1>
          <Button variant="contained" onClick={()=>addEmployeeBtn()}>Add Employee</Button>
          <DialogBox
            maxWidth="md" 
            title={'Add Employee'}
            cancelBtn={'cancel'}
            submitBtn={'submit'}
            openModal={popupModel} 
            closeModel={addEmployeeBtn} 
            onSubmit={handleSubmit}
            modelData={
              <EmployeeForm 
              cities={cities}
              skills={skills}
              errors={formErrors}
              initialValues={initialValues}
              handleChange={handleChange} 
              />
            } 
          />
        </div>
        {empData?.length ? <EmployeeList 
        deleteModel={deleteModel} 
        deleteModelPop={deleteModelPop}
        handleDelete={handleDelete}
        fullName={title}
        getData={handelEdit} data={empData} /> : 'Loading...' }
      </div>
    </main>
  )

}

export default Employee








