import React, { useState } from 'react'

const Form = () => {

    const [todo, setTodo] = useState('')

    const handelChange = (value) => {
        setTodo(value.target.value)
        console.log(todo.message)
    }

    return(
        <>
            <form>
                <input type="text" 
                onChange={(e)=>handelChange(e)} 
                value={todo.message} />
            </form>
        </>
    )
}

export default Form
