import React, { useState } from 'react';
import shortid from 'shortid';

const Todo = () => {
    const [inputValue, setInputValue] = useState('');
    const [formValid, setFormValid] = useState(false);
    const [error, setError] = useState(false);
    const [todoList, setTodoList] = useState([]);
    const danger = { color: 'red' }

    const handelOnChange = (event) => {
        setInputValue(event.target.value)
        // console.log(event.target.value)
    }

    const handelSubmit = (event) => {
        event.preventDefault()
        if (inputValue.length !== 0) {
            if(inputValue.length < 3){
                setError(true)
                setFormValid(false)
            }else {
                console.log("YES, you can save")
                setInputValue('')
                setTodoList([...todoList, {id:shortid.generate(), message: inputValue}])
                setFormValid(false)
                setError(false)
            }
        } else {
            console.log("SORRY")
            setError(false)
            setFormValid(true)
        }
        console.log(todoList)
    }

    const handelDelete = (getId) => {
        const indexValue = todoList.findIndex((data)=>(data.id === getId))
        console.log(indexValue)
        todoList.splice(indexValue, 1)
        // setTodoList([...todoList])
    }

    return(
        <>
            <h1>Todo Message</h1>
            <form>
                <input 
                type={'text'} 
                value={inputValue} 
                placeholder={'Add todo'} 
                onChange={handelOnChange}
                />
                <button onClick={handelSubmit}>Add todo</button>
                <br />
                {formValid && <small style={danger}>Please type message</small>}
                {error && <small style={danger}>Please enter minimum 3 charector</small>}
            </form>

            {todoList?.length !== 0 ? (
            <ol>
                {
                   todoList?.map((value, index)=>(
                    <li key={value.id}>
                        <span>{value.message}</span>
                        <span>
                            <button onClick={()=>handelDelete(value.id)}>Delete</button>
                            {/* <button onClick={()=>handelEdit(value.id)}>Edit</button> */}
                        </span>
                    </li>
                   )) 
                }
            </ol>
            ) : (
                <h4>There is no data</h4>
            )}
        </>
    )
}

export default Todo
