import React, { useState } from 'react'

const Form = () => {

    const [todo, setTodo] = useState({
        message: ""
    })

    const handelChange = (key, value) => {
        setTodo((prevValue)=>({...prevValue, [key]: value}))
    }

    return(
        <>
            <form>
                <input type="text" 
                onChange={(e)=>handelChange('message', e)} 
                value={todo.message} />
            </form>
        </>
    )
}

export default Form
