import { FormControl, MenuItem, Select } from '@mui/base'
import { FormControlLabel, InputLabel, Radio, RadioGroup, TextField } from '@mui/material'
import React, { useState } from 'react'
import styles from './employeeForm.module.scss'

const EmployeeForm = () => {
    // const cities = ['delhi', 'mumbai', 'banglore', 'kolkata', 'pune'];

    const [userData, setUserData] = useState({
        fullName: "",
        email: "",
        contact: "",
        city: "",
        skill: "",
        gender: ""
    });

    // const handleChange = (key, value) => {
    //     setUserData((prevValue) => ({...prevValue, [key]: value}));
    // }

    const [age, setAge] = React.useState('');

    const handleChange = (event) => {
        setAge(event.target.value);
    };

  return (
    <form>
      <div className={styles['form']}>
        <div className={styles.formRow}>
            <label htmlFor="name">Name</label>
            <TextField variant="outlined" type='text' placeholder='Enter Full Name' id="name" name="fullName" />
        </div>
        <div className={styles.formRow}>
            <label htmlFor="name">Email</label>
            <TextField variant="outlined" type='text' placeholder='Enter email id' id="name" name="email" />
        </div>
        <div className={styles.formRow}>
            <label htmlFor="name">Contact Number</label>
            <TextField variant="outlined" type='text' placeholder='Enter contact number' id="name" name="fullName" />
        </div>
        <div className={styles.formRow}>
            <label htmlFor="name">City</label>
            {/* <FormControl fullWidth>
                <InputLabel className={styles['cityLab']} id="city">Select City</InputLabel>
                <Select
                    labelId="city"
                    id="city"
                    value={userData.city}
                    label="City"
                    onChange={(e) => handleChange('city', e.target.value)}
                >
                    {cities.map((cityName, index) => (
                        <MenuItem key={index} value={cityName}>{cityName}</MenuItem>
                    ))}
                </Select>
            </FormControl> */}
            {/* <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Age</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={age}
                    label="Age"
                    onChange={handleChange}
                >
                    <MenuItem value={10}>Ten</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                </Select>
            </FormControl> */}
        </div>
        <div className={styles.userRow}>
                    <label htmlFor="gender">Gender</label>
                    <RadioGroup
                        row
                        defaultValue="female"
                        name="gender"
                        value={userData.gender}
                        onChange={(e) => handleChange('gender', e.target.value)}
                        >
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                        <FormControlLabel value="other" control={<Radio />} label="Other" />
                    </RadioGroup>
                </div>
      </div>
    </form>
  )
}

export default EmployeeForm
