import { Button, Checkbox, FormControlLabel, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'
import axios from 'axios';

const Login = () => {

  const formInitialVal = { email: '', password: '' }
  const [initialValue, setInitialValue] = useState(formInitialVal)

  const handleChange = (e) => {
    const {name, value} = e.target;
    setInitialValue({ ...initialValue, [name]: value })
  }

  const handleSubmit = () => {
    axios.post(`${'http://localhost:3100/api/login'}`, initialValue)
    .then(res => {
      console.log("ðŸš€ ~ file: index.js:18 ~ handleSubmit ~ res:", res)
      if(res.status === 204){

      }
    })
    .catch(err => console.log(err))
  }

  return (
    <form style={{width: '100%'}}>
        <Typography component="h1" variant="h5">
        Sign In
        </Typography>
        <TextField
        margin="normal"
        required
        fullWidth
        id="email"
        label="Email Address"
        name="email"
        autoComplete="email"
        autoFocus
        onChange={handleChange}
        />
        <TextField
        margin="normal"
        required
        fullWidth
        name="password"
        label="Password"
        type="password"
        id="password"
        autoComplete="current-password"
        onChange={handleChange}
        />
        {/* <FormControlLabel
        control={<Checkbox value="remember" color="primary" />}
        label="Remember me"
        /> */}
        <Button
        type="button"
        fullWidth
        variant="contained"
        sx={{ mt: 3, mb: 2 }}
        onClick={handleSubmit}
        >
        Sign In
        </Button>
    </form>
  )
}

export default Login
