import React, { useState } from 'react'
import { FormControlLabel, FormLabel, InputLabel, MenuItem, Radio, RadioGroup, Select, TextField } from '@mui/material'
import styles from './employeeForm.module.scss'
import { FormControl } from '@mui/base'

const EmployeeForm = () => {

    const cities = ['delhi', 'mumbai', 'banglore', 'kolkata', 'pune'];
    const [userData, setUserData] = useState({
        fullName: "",
        email: "",
        contact: "",
        city: "",
        skill: "",
        gender: ""
    });

    const handleChange = (key, value) => {
        setUserData((prevValue) => ({...prevValue, [key]: value}));
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        let index = -1;
        let userList = [];
        if (localStorage.getItem("users-data") !== null) {
            let dataval = JSON.parse(localStorage.getItem("users-data"))
            if(index === -1){
                dataval.push(userData);
                localStorage.setItem("users-data", JSON.stringify(dataval));
            }
        }else {
            userList.push(userData);
            localStorage.setItem("users-data", JSON.stringify(userList));
        }
    };

  return (
    <form onSubmit={handleSubmit}>
      <div className={styles['form']}>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel>Name</FormLabel>
                <TextField variant="outlined" type='text' placeholder='Enter Full Name' id="name" name="fullName" />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel>Email</FormLabel>
                <TextField variant="outlined" type='email' placeholder='Enter email id' id="email" name="email" />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel>Contact Number</FormLabel>
                <TextField variant="outlined" type='tel' placeholder='Enter contact number' id="number" name="number" />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <label htmlFor="city">City</label>
            <FormControl fullWidth>
            <InputLabel className={styles['cityLab']} id="city">Select City</InputLabel>
            <Select
                labelId="city"
                id="city"
                value={userData.city}
                label="City"
                onChange={(e) => handleChange('city', e.target.value)}
            >
                {cities.map((cityName, index) => (
                    <MenuItem key={index} value={cityName}>{cityName}</MenuItem>
                ))}
            </Select>
        </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel>Skills</FormLabel>
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel>Gender</FormLabel>
                <RadioGroup
                    row
                    aria-labelledby="demo-row-radio-buttons-group-label"
                    name="row-radio-buttons-group"
                >
                    <FormControlLabel value="female" control={<Radio />} label="Female" />
                    <FormControlLabel value="male" control={<Radio />} label="Male" />
                    <FormControlLabel value="other" control={<Radio />} label="Other" />
                </RadioGroup>
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel>Status</FormLabel>
            </FormControl>
        </div>
      </div>
    </form>
  )
}

export default EmployeeForm
