import React, { useState } from 'react'

const Form = () => {

    const [todo, setTodo] = useState({
        firstName: ""
    })

    const handelChange = (key, value) => {
        console.log(key, value)
    }

    return(
        <>
            <form>
                <input type="text" 
                onChange={(e)=>handelChange("firstName", e.target.value)} 
                value={todo.firstName} />
            </form>
        </>
    )
}

export default Form


