import * as Yup from "yup";
export const createEmployeeSchema = Yup.object({
  fullName: Yup.string().min(3).max(30).required('Please Enter your name.'),
  email: Yup.string().email().required('Please enter your email'),
  contactNumber: Yup.number().min(10).required("Please enter contact number"),
  dateOfBirth: Yup.string().required('Please Select Date of Birth.'),
  city: Yup.string().required('Please Select City.'),
  skill: Yup.string().required('This field is required.'),
  gender: Yup.string().required('This field is required.'),
  isActive: Yup.boolean().required('This field is required.')
});
