import React, { useState } from 'react'
import { Button, Checkbox, FormControlLabel, FormLabel, InputLabel, MenuItem, Radio, RadioGroup, Select, TextField } from '@mui/material'
import { FormControl } from '@mui/base'
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DatePicker, LocalizationProvider } from '@mui/x-date-pickers'
import styles from './employeeForm.module.scss'


const EmployeeForm = (props) => {
    const [userData, setUserData] = useState({
        fullName: "",
        email: "",
        contact: "",
        city: "",
        skill: "",
        gender: ""
    });

    const handleChange = (key, value) => {
        setUserData((prevValue) => ({...prevValue, [key]: value}));
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        let index = -1;
        let userList = [];
        if (localStorage.getItem("users-data") !== null) {
            let dataval = JSON.parse(localStorage.getItem("users-data"))
            if(index === -1){
                dataval.push(userData);
                localStorage.setItem("users-data", JSON.stringify(dataval));
            }
        }else {
            userList.push(userData);
            localStorage.setItem("users-data", JSON.stringify(userList));
        }
    };

  return (
    <div className={styles['form-parent']}>
        <h2>{title}</h2>
        <form onSubmit={handleSubmit}>
            <div className={styles['userForm']}>
                <div className={styles.userRow}>
                    <label htmlFor="name">Name</label>
                    <TextField 
                    variant="outlined" 
                    type='text' 
                    placeholder='Enter Full Name'
                    id="name" 
                    name="fullName"
                    value={userData.fullName}
                    onChange={(e) => handleChange('fullName', e.target.value)}
                    />
                </div>
                <div className={styles.userRow}>
                    <label htmlFor="email">Email</label>
                    <TextField 
                    variant="outlined" 
                    type='email' 
                    placeholder='Enter Email id' 
                    id="email" 
                    name="email"
                    value={userData.email}
                    onChange={(e) => handleChange('email', e.target.value)}
                    />
                </div>
                <div className={styles.userRow}>
                    <label htmlFor="contact">Contact Number</label>
                    <TextField 
                    id="contact" 
                    variant="outlined" 
                    type='tel' 
                    placeholder='Enter Contact Number'
                    name="contact"
                    value={userData.contact}
                    onChange={(e) => handleChange('contact', e.target.value)}
                    />
                </div>
                <div className={styles.userRow}>
                    <label htmlFor="city">City</label>
                    <FormControl fullWidth>
                        <InputLabel className={styles['cityLab']} id="city">Select City</InputLabel>
                        <Select
                            labelId="city"
                            id="city"
                            value={userData.city}
                            label="City"
                            onChange={(e) => handleChange('city', e.target.value)}
                        >
                            {cities.map((cityName, index) => (
                                <MenuItem key={index} value={cityName}>{cityName}</MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                </div>
                <div className={styles.userRow}>
                    <label htmlFor="skill">Skill</label>
                    <Select 
                    id="skill"
                    name="skill"
                    value={userData.skill}
                    onChange={(e) => handleChange('skill', e.target.value)}
                    >
                        {skills.map((skill, index)=>(
                            <MenuItem key={index} value={skill}>{skill}</MenuItem>
                        ))}
                    </Select>
                </div>
                {/* <div className={styles.userRow}>
                    <MultipleSelect 
                    id="skill"
                    name="skill"
                    value={userData.skill}
                    onChange={(e) => handleChange('skill', e.target.value)}
                    />
                </div> */}
                <div className={styles.userRow}>
                    <label htmlFor="gender">Gender</label>
                    <RadioGroup
                        row
                        defaultValue="female"
                        name="gender"
                        value={userData.gender}
                        onChange={(e) => handleChange('gender', e.target.value)}
                        >
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                        <FormControlLabel value="other" control={<Radio />} label="Other" />
                    </RadioGroup>
                </div>
                <div className={styles.userRow}>
                    <FormControlLabel 
                    control={<Checkbox />} 
                    // defaultChecked
                    label="This is active user" 
                    name="isActive"
                    value={userData.isActive}
                    onChange={(value) => handleChange('isActive', !value)}
                    />
                </div>
            </div>
            <DialogActions style={{ padding: '0' }}>
            <Button type='button' autoFocus variant="outlined" color="error">
            Cancle
            </Button>
            <Button autoFocus type="submit" variant="contained">Submit</Button>
            </DialogActions>
        </form>
    </div>
  )
}

export default EmployeeForm
