import React, { useState } from 'react';
import Avatar from '@mui/joy/Avatar';
import Chip from '@mui/joy/Chip';
import Button from '@mui/joy/Button';
import ButtonGroup from '@mui/joy/ButtonGroup';
import Card from '@mui/joy/Card';
import CardContent from '@mui/joy/CardContent';
import CardOverflow from '@mui/joy/CardOverflow';
import CardActions from '@mui/joy/CardActions';
import Typography from '@mui/joy/Typography';
import { DialogBox } from '../DialogBox';
import styles from'./profileCard.module.scss'

const ProfileCard = (props) => {
  const { data } = props;
  const {profileImg, fullName, contactNumber, email, gender, dateOfBirth, city, skill, message, isActive} = data;

  const [popupModel, setPopupModel] = useState(false)
  const moreDetailBtn = () => { setPopupModel(!popupModel) }

  const CustomLink = (props) => <a {...props}>{props.children}</a>;

  const viewAllData = (
    <ul className={styles.dataList}>
      <li><strong>Name : </strong> {fullName}</li>
      <li><strong>Number :</strong> {contactNumber}</li>
      <li><strong>Email : </strong> {email}</li>
      <li><strong>Gender : </strong> {gender}</li>
      <li><strong>Date Of Birth : </strong> {dateOfBirth}</li>
      <li><strong>City : </strong> {city}</li>
      <li><strong>Skill : </strong> {
        skill?.map((val, index)=>(<span key={index}>{val}{', '}</span>))
      }</li>
      <li><strong>Message : </strong> {message}</li>
    </ul>
  )

  return (
    <>
      <Card>
        <CardContent sx={{ alignItems: 'center', textAlign: 'center' }}>
          {props.children}
          {/* <Avatar src={profileImg} sx={{ '--Avatar-size': '4rem' }} />
          <Chip
            size="sm"
            variant="soft"
            color={isActive ? 'primary' : 'secondary'}
            sx={{
              mt: -1,
              mb: 1,
              border: '3px solid',
              borderColor: 'background.surface',
            }}
          >
            {isActive ? 'PRO' : 'Normal'}
          </Chip> */}
          <Typography level="title-lg">{fullName}</Typography>
          <Typography level="body-sm" sx={{ maxWidth: '100%' }}>
            {message}
          </Typography>
        </CardContent>
        {/* <CardOverflow sx={{ bgcolor: 'background.level1' }}>
          <CardActions buttonFlex="1">
            <ButtonGroup variant="outlined" sx={{ bgcolor: 'background.surface' }}>
              <Button href={"tel:" + contactNumber} component={CustomLink} variant="outlined">Connect</Button>
              <Button onClick={() => moreDetailBtn()}>More Detail</Button>
            </ButtonGroup>
          </CardActions>
        </CardOverflow> */}
      </Card>
      {/* <DialogBox
        maxWidth="sm" 
        title={'More Detail'}
        cancelBtn={'Close'}
        openModal={popupModel} 
        closeModel={moreDetailBtn}
        modelData={viewAllData} 
      /> */}
    </>
  )
}

export default ProfileCard
