import React, {useState} from 'react';
import shortid from 'shortid';

export function Form() {
    const [todo, setTodo] = useState('');
    const [todoList, setTodoList] = useState([]);
    const [buttonFlag, setButtonFlag] = useState(true)

    const handelSubmit = (e) => {
        e.preventDefault()
        if(!todo){
            alert("Todo is empty!")
        }else {
            setTodoList([...todoList, {id:shortid.generate(), name: todo}])
            setTodo('')
            console.log(todoList)
        }
    }

    const handelDelete = (id) => {
        const indexValue = todoList.findIndex((item)=>item.id === id);
        todoList.splice(indexValue, 1); 
        setTodoList([...todoList]);
    }

    const handelEdit = (value) => {
        console.log(value)
        // setTodo(value.name)
        // idFlag = value.id
    }

    return(
        <>
        <form>
            <input 
                type={'text'}
                placeholder={'Add todo'}
                value={todo}
                onChange={(e)=>setTodo(e.target.value)}
            />
            <button onClick={handelSubmit} type={'submit'}>buttonFlag ? Add Todo</button>
        </form>
        <ul>
            {todoList?.map((item, index)=>(
                <li key={item.id}>
                    <span>{item.name}</span>
                    <span>
                        <button onClick={()=>handelDelete(item.id)} type={'button'}>Delete</button>
                        <button onClick={()=>handelEdit(item)}>Edit</button>
                    </span>
                </li>
            ))}
        </ul>
        </>
    )
}



