import React, { useState } from 'react'

const Form = () => {

    const [todo, setTodo] = useState()

    const handelChange = (event) => {
        setTodo({
            firstName: event.target.value
        })
    }

    return(
        <>
            <form>
                <input type="text" 
                onChange={(e)=>handelChange("firstName", e)} 
                value={todo.firstName} />
            </form>
        </>
    )
}

export default Form
