import React, { useState } from 'react';
import shortid from 'shortid';

const Todo = () => {
    const [inputValue, setInputValue] = useState('');
    const [todoList, setTodoList] = useState([]);
    const [formValid, setFormValid] = useState(false);
    const [error, setError] = useState(false);
    const [getId, setId] = useState(null)
    const danger = { color: 'red' }

    const handelOnChange = (event) => {
        setInputValue(event.target.value)
        // console.log(event.target.value)
    }

    const handelSubmit = (event) => {
        event.preventDefault()
        if (inputValue.length !== 0) {
            if(inputValue.length < 3){
                setError(true)
                setFormValid(false)
            }else {
                if(!getId){
                    setTodoList([...todoList, {id:shortid.generate(), message: inputValue}])
                    setInputValue('')
                    setFormValid(false)
                    setError(false)

                    let tempList = todoList;
                    tempList.push(...todoList, {id:shortid.generate(), message: inputValue})
                    localStorage.setItem('todos', JSON.stringify(tempList))

                }else {
                    setTodoList(
                        todoList?.map((data)=>{
                            if(data.id === getId){
                                return{...data, message:inputValue}
                            }
                            return data;
                        })
                    )
                    setError(false)
                    setFormValid(false)
                    setId(null)
                    setInputValue('')
                }
            }
        } else {
            setError(false)
            setFormValid(true)
        }
    }

    const handelDelete = (_id) => {
        const indexValue = todoList.findIndex((data)=>(data.id === _id))
        todoList.splice(indexValue, 1)
        setTodoList([...todoList])
    }

    const handelEdit = (_id) => {
        const getValue = todoList.find((data)=>(data.id === _id))
        setInputValue(getValue.message)
        setId(getValue.id)
        // console.log(_id)
    }

    return(
        <>
            <h1>Todo Message</h1>
            <form>
                <input 
                type={'text'} 
                value={inputValue} 
                placeholder={'Add todo'} 
                onChange={handelOnChange}
                />
                <button onClick={handelSubmit}>{getId ? 'Edit' : 'Add'} todo</button>
                <br />
                {formValid && <small style={danger}>Please type message</small>}
                {error && <small style={danger}>Please enter minimum 3 charector</small>}
            </form>

            {todoList?.length !== 0 ? (
            <ol>
                {
                   todoList?.map((value, index)=>(
                    <li key={value.id}>
                        <span>{value.message}</span>
                        <span>
                            <button onClick={()=>handelDelete(value.id)}>Delete</button>
                            <button onClick={()=>handelEdit(value.id)}>Edit</button>
                        </span>
                    </li>
                   )) 
                }
            </ol>
            ) : (
                <h4>There is no data</h4>
            )}
        </>
    )
}

export default Todo
