import { Box, Button, FormControl, Grid, InputAdornment, InputLabel, MenuItem, Select, TextField, Typography } from '@mui/material'
import React from 'react'

const EmployeeForm = () => {
    return (
        <>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', alignItems: 'center', justifyContent: 'space-between' }}>
                <Typography variant="h5" component="h2">Add Employee</Typography>
            </Box>
            <Grid container>
                <Grid item xs={4}>
                    <form style={{ width: '100%' }}>
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="text"
                            label="User Name"
                            name="name"
                            autoComplete="off"
                            autoFocus
                        // error={Boolean(formErrors.name)}
                        // helperText={formErrors.name}
                        // value={initialValue.name}
                        // onChange={handleChange}
                        />
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="email"
                            label="Email Address"
                            name="email"
                            autoComplete="off"
                        // error={Boolean(formErrors.email)}
                        // helperText={formErrors.email}
                        // value={initialValue.email}
                        // onChange={handleChange}
                        />
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="phone"
                            label="Enter Mobile Number"
                            name="phone"
                            autoComplete="off"
                            InputProps={{
                                startAdornment: <InputAdornment position='start'>+91</InputAdornment>
                            }}
                        // error={Boolean(formErrors.phone)}
                        // helperText={formErrors.phone}
                        // value={initialValue.phone}
                        // onChange={handleChange}
                        />
                        <FormControl fullWidth sx={{ mt: 2, minWidth: 100 }}>
                            <InputLabel id="demo-simple-select-autowidth-label">Age</InputLabel>
                            <Select
                                labelId="demo-simple-select-autowidth-label"
                                id="demo-simple-select-autowidth"
                                // value={age}
                                // onChange={handleChange}
                                autoWidth
                                label="Age"
                            >
                                <MenuItem value="">
                                    <em>Designation</em>
                                </MenuItem>
                                <MenuItem value={10}>React Developer</MenuItem>
                                <MenuItem value={21}>Angular Developer</MenuItem>
                                <MenuItem value={22}>UI Developer</MenuItem>
                                <MenuItem value={22}>Java Developer</MenuItem>
                                <MenuItem value={22}>IOS Developer</MenuItem>
                                <MenuItem value={22}>VUE Developer</MenuItem>
                                <MenuItem value={22}>PHP Developer</MenuItem>
                            </Select>
                        </FormControl>
                        <Button
                            type="button"
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                        // onClick={handleSubmit}
                        >
                            Sign Up
                        </Button>
                    </form>
                </Grid>
            </Grid>
        </>
    )
}

export default EmployeeForm
