import React, { useState, useEffect } from 'react'
import { Button } from '@mui/material'
import { DialogBox } from '../genericComponent/DialogBox'
import EmployeeForm from './EmployeeForm'
import shortid from 'shortid';
import EmployeeList from './EmployeeList';

const cities = ['delhi', 'mumbai', 'bangalore', 'kolkata', 'pune'];
const skills = ['angular', 'react', 'vue', 'nextjs', 'drupal', 'html5', 'css3', 'sass', 'mui', 'material-ui'];


const Employee = () => {

  const sortId = shortid.generate();
  const localStore = localStorage.getItem('empList');
  const empData = JSON.parse(localStore)

  const dataVal = { 
    // profileImg: '',
    fullName: '', 
    email: '', 
    contactNumber: '', 
    dateOfBirth: '', 
    city: '',
    skill: [],
    gender: '',
    message: '',
    isActive: '',
    id:sortId,
  }

  const [popupModel, setPopupModel] = useState(false)
  const addEmployeeBtn = () => { setPopupModel(!popupModel) }
  const [getDeleteData, setGetDeleteData] = useState();
  const [popupDeleteModel, setPopupDeleteModel] = useState(false);

  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit ] = useState(false);
  const [initialValues, setInitialValues] = useState(dataVal);
  // const [empData, setEmpData] = useState(JSON.parse(localStorage.getItem('empList') || ''));


  const handleChange = (e) => {
    const {name, value, checked, files} = e.target;     
    // if (name === "profileImg") {
    //   console.log(URL.createObjectURL(files[0]))
    //   setInitialValues({...initialValues, 'profileImg': files[0]})
    // }
    // else 
    if(name === "isActive"){
      setInitialValues({ ...initialValues, [name]: checked })
    }
    else {
      setInitialValues({ ...initialValues, [name]: value })
    }
  }

  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;

    // if (!values.profileImg) {
    //   errors.profileImg = "This field is required!"
    // }

    if (!values.fullName) {
      errors.fullName = "This field is required!"
    } else if (values.fullName.length < 3) {
      errors.fullName = "Name should be minimum 3 character."
    }

    if (!values.email) {
      errors.email = "This field is required!"
    }else if (!regex.test(values.email)){
      errors.email = "This is not a valid email."
    }

    if (!values.contactNumber) {
      errors.contactNumber = "This field is required!"
    } else if (values.contactNumber.length < 10){
      errors.contactNumber = "Contact number minimum 10 digits."
    } else if (values.contactNumber.length > 10){
      errors.contactNumber = "Contact number maximum 10 digits."
    }

    if (!values.dateOfBirth) {
      errors.dateOfBirth = "This field is required!"
    }

    if (!values.city) {
      errors.city = "This field is required!"
    }

    if (!values?.skill?.length) {
      errors.skill = "This field is required!"
    }

    if (!values.message) {
      errors.message = "This field is required!"
    } else if (values.message.length < 10){
      errors.message = "Message length minimum 10 letters"
    } else if (values.message.length > 250){
      errors.message = "Message length should be maximum 250 letters."
    }

    if (!values.gender) {
      errors.gender = "This field is required!"
    }

    // if (!values.isActive) {
    //   errors.isActive = "This field is required!"
    // }

    return errors;
  }

  useEffect(() => {
    if(Object.keys(formErrors).length === 0 && isSubmit){
      setIsSubmit(false)
    }
  }, [formErrors, isSubmit])

  useEffect(() => {
    if (isSubmit) {
      const errors = validate(initialValues)
      setFormErrors(errors)      
    }
  }, [formErrors])



  const name = getDeleteData?.fullName;
  const moreDetailBtn = (val) => { 
    setPopupDeleteModel(!popupDeleteModel) 
    setGetDeleteData(val)
  }
  const handleDelete = (val) => {
    console.log(val)
  }


  const handelEdit = (id) => {
    const getValue = empData.find((data)=>(data.id === id))
    setInitialValues(getValue)
    setPopupModel(true)
    console.log("ðŸš€ ~ file: index.js:128 ~ getFormData ~ getValue:", getValue)
  }

  const handleSubmit = () => {
    const errors = validate(initialValues)
    setFormErrors(errors)
    setIsSubmit(true)
    console.log(validate(initialValues))
    if (Object.keys(errors)?.length) {
      return false
    } 
    else if (JSON.parse(localStore) !== null) { 
      console.log("Have data")
      JSON.parse(localStore).map(data => {
        if(data.id !== sortId){
          const oldArr = JSON.parse(localStore)
          oldArr.push(initialValues)
          localStorage.setItem('empList', JSON.stringify(oldArr))
        }
      })
    }
    else {
      console.log("Empty")
      const storeInArr = []
      storeInArr.push(initialValues)
      localStorage.setItem('empList', JSON.stringify(storeInArr))
    }
    setInitialValues(dataVal)
    setFormErrors({})
    setIsSubmit(false)
    setPopupModel(false)
  }

  return (
    <>
      <div className='d-flex align-items-center justify-content-between'>
        <h1>Employee List </h1>
        <Button variant="contained" onClick={()=>addEmployeeBtn()}>Add Employee</Button>
        <DialogBox
          maxWidth="md" 
          title={'Add Employee'}
          cancelBtn={'cancel'}
          submitBtn={'submit'}
          openModal={popupModel} 
          closeModel={addEmployeeBtn} 
          onSubmit={handleSubmit}
          modelData={
            <EmployeeForm 
            cities={cities}
            skills={skills}
            errors={formErrors}
            initialValues={initialValues}
            handleChange={handleChange} 
            />
          } 
        />
      </div>
      {empData?.length ? <EmployeeList getDeleteData={handleDelete} getData={handelEdit} data={empData} /> : 'Sorry we have no data' }

    </>
  )

}

export default Employee
