import React, { useState } from 'react';
import shortid from 'shortid';

import messageList from "./messageList";

const Todo = () => {
    const [inputValue, setInputValue] = useState('');
    const [todoList, setTodoList] = useState([]);
    const [formValid, setFormValid] = useState(false);
    const [error, setError] = useState(false);
    const [getId, setId] = useState(null)
    const [successMsg, setSuccessMsg] = useState(false)
    const danger = { color: 'red' }

    const handelOnChange = (event) => {
        setInputValue(event.target.value)
        // console.log(event.target.value)
    }

    const handelSubmit = (event) => {
        event.preventDefault()
        if (inputValue.length !== 0) {
            if(inputValue.length < 3){
                setError(true)
                setFormValid(false)
            }else {
                if(!getId){
                    setTodoList([...todoList, {id:shortid.generate(), message: inputValue}])
                    setInputValue('')
                    setFormValid(false)
                    setError(false)

                }else {
                    setTodoList(
                        todoList?.map((data)=>{
                            if(data.id === getId){
                                return{...data, message:inputValue}
                            }
                            return data;
                        })
                    )
                    setError(false)
                    setFormValid(false)
                    setId(null)
                    setInputValue('')
                }
            }
        } else {
            setError(false)
            setFormValid(true)
        }
    }



    return(
        <>
            {successMsg && <div className='successfully'><strong>Delete</strong> Successfully!</div>}
            <h1>Todo Message</h1>
            <form>
                <input 
                type={'text'} 
                value={inputValue} 
                placeholder={'Add todo'} 
                onChange={handelOnChange}
                />
                <button onClick={handelSubmit}>{getId ? 'Edit' : 'Add'} todo</button>
                <br />
                {formValid && <small style={danger}>Please type message</small>}
                {error && <small style={danger}>Please enter minimum 3 charector</small>}
            </form>


        </>
    )
}

export default Todo
