import React, { useState } from 'react'
import { styled } from '@mui/material/styles';
import { IconButton, Stack } from '@mui/material';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import styles from'../employee.module.scss'
import ProfileCard from '../../genericComponent/profile-card/ProfileCard';
import { DialogBox } from '../../genericComponent/DialogBox';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

const EmployeeList = (props) => {
  const { data } = props;
  const [empData, setEmpData] = useState(JSON.parse(localStorage.getItem('empList') || ''));
  const [getData, setGetData] = useState();
  const [popupModel, setPopupModel] = useState(false)
  const name = getData?.fullName;

  const moreDetailBtn = (val) => { 
    setPopupModel(!popupModel) 
    setGetData(val)
  }

  const handleDelete = () => {
    const indexValue = empData?.findIndex((data)=>(data.id === getData.id))
    empData.splice(indexValue, 1)
    setEmpData(empData)
    console.log(empData)
    localStorage.setItem('empList', JSON.stringify(empData))
    setPopupModel(false)
  }

  return (
    <>
      <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={2}>
          {data?.map((value, index) => (
            <Grid item xs={4} key={value.id}>
              <Item style={{padding: '0', borderRadius: '0', boxShadow: 'none', height: '100%', display: 'grid'}}>
                <ProfileCard data={value}>
                  <Stack direction="row" spacing={1} className={styles.iconPosition}>
                    <IconButton aria-label="edit" color="primary" size="small" onClick={()=>props.getData(value.id)} sx={{ ml: 2 }}>
                      <EditIcon fontSize="inherit" />
                    </IconButton>
                    <IconButton aria-label="delete" color="secondary" size="small" onClick={() => moreDetailBtn(value)}>
                      <DeleteIcon fontSize="inherit" />
                    </IconButton>
                  </Stack>
                </ProfileCard>
              </Item>
            </Grid>
          ))}
        </Grid>
      </Box>

      <DialogBox
      maxWidth="xs" 
      title={'You want to delete this employee?'}
      cancelBtn={'Close'}
      submitBtn={'Yes'}
      openModal={popupModel} 
      closeModel={moreDetailBtn}
      modelData={name} 
      onSubmit={handleDelete}
      />

    </>
  )
}

export default EmployeeList
