import { Box, Button, FormControl, Grid, InputAdornment, InputLabel, MenuItem, Select, Stack, TextField, Typography } from '@mui/material'
import { DatePicker, LocalizationProvider } from '@mui/x-date-pickers'
import { DemoContainer } from '@mui/x-date-pickers/internals/demo';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import React, { useState } from 'react'
import { DatepickerWrapper } from './formStyled';

const EmployeeForm = () => {

    const [imgPreview, setImgPreview] = useState(null)

    const handleUploadImg = (e) => {
        const reader = new FileReader();
        reader.readAsDataURL(e.target.files[0]);
        reader.onload = (e) => {
            setImgPreview(e.target.result)
        }


        // const url = reader.readAsDataURL(file);
        // console.log("ðŸš€ ~ file: index.js:14 ~ handleUploadImg ~ url:", url)
    }

    return (
        <>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', alignItems: 'center', justifyContent: 'space-between' }}>
                <Typography variant="h5" component="h2">Add Employee</Typography>
            </Box>
            <Grid container>
                <Grid item xs={4}>
                    <form>

                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="text"
                            label="User Name"
                            name="name"
                            autoComplete="off"
                            autoFocus
                        // error={Boolean(formErrors.name)}
                        // helperText={formErrors.name}
                        // value={initialValue.name}
                        // onChange={handleChange}
                        />

                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="email"
                            label="Email Address"
                            name="email"
                            autoComplete="off"
                        // error={Boolean(formErrors.email)}
                        // helperText={formErrors.email}
                        // value={initialValue.email}
                        // onChange={handleChange}
                        />

                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            id="phone"
                            label="Enter Mobile Number"
                            name="phone"
                            autoComplete="off"
                            InputProps={{
                                startAdornment: <InputAdornment position='start'>+91</InputAdornment>
                            }}
                        // error={Boolean(formErrors.phone)}
                        // helperText={formErrors.phone}
                        // value={initialValue.phone}
                        // onChange={handleChange}
                        />

                        <DatepickerWrapper>
                            <LocalizationProvider dateAdapter={AdapterDayjs}>
                                <DemoContainer components={['DatePicker']}>
                                    <DatePicker label="Select Date of Birth" />
                                </DemoContainer>
                            </LocalizationProvider>
                        </DatepickerWrapper>

                        <FormControl fullWidth sx={{ mt: 2, minWidth: 100 }}>
                            <InputLabel id="designation">Designation</InputLabel>
                            <Select
                                labelId="designation"
                                id="designation"
                                // value={age}
                                // onChange={handleChange}
                                autoWidth
                                label="Designation"
                            >
                                <MenuItem value=""><em>Designation</em></MenuItem>
                                <MenuItem value="react developer">React Developer</MenuItem>
                                <MenuItem value='angular developer'>Angular Developer</MenuItem>
                                <MenuItem value='ui developer'>UI Developer</MenuItem>
                                <MenuItem value='java developer'>Java Developer</MenuItem>
                                <MenuItem value='ios developer'>IOS Developer</MenuItem>
                                <MenuItem value='vue developer'>VUE Developer</MenuItem>
                                <MenuItem value='php developer'>PHP Developer</MenuItem>
                            </Select>
                        </FormControl>

                        <FormControl fullWidth sx={{ mt: 2, minWidth: 100 }}>
                            <Stack direction="row" alignItems="center" spacing={2}>

                                <Button variant="contained" component="label" onChange={handleUploadImg}>
                                    Upload
                                    <input hidden accept="image/*" multiple type="file" />
                                </Button>
                            </Stack>
                        </FormControl>

                        <Button
                            type="button"
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                        // onClick={handleSubmit}
                        >
                            Sign Up
                        </Button>

                    </form>
                </Grid>
            </Grid>
        </>
    )
}

export default EmployeeForm
