import React from 'react'
import { Checkbox, FormControlLabel, FormLabel, InputLabel, MenuItem, Radio, RadioGroup, Select, TextField } from '@mui/material'
// import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
// import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
// import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { FormControl } from '@mui/base'
import styles from './employeeForm.module.scss'

const EmployeeForm = (props) => {
    const {cities, skills, inputValue, handelOnChange} = props

  return (
    <form>
      <div className={styles['form']}>
        <div className={styles.formRow}>
            <FormControl>
                {/* <FormLabel htmlFor='fullName'>Full Name</FormLabel> */}
                <TextField
                fullWidth
                variant='outlined'
                type='text'
                id='fullName'
                label="Full Name"
                placeholder='Enter Full Name'
                name="fullName"
                value={inputValue.fullName}
                onChange={(e)=>handelOnChange('fullName', e.target.value)}
                // inputRef={require({
                //     required: "This Field is required."
                // })}
                required
                error={Boolean(false)}
                helperText={"This Field is required."}
                />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                {/* <FormLabel htmlFor="email">Email</FormLabel> */}
                <TextField
                fullWidth
                variant='outlined'
                type='email'
                id='email'
                label="Email"
                placeholder='Enter Email Id'
                name="email"
                value={inputValue.email}
                onChange={(e)=>handelOnChange('email', e.target.value)}
                // error
                // helperText="This field is required."
                />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                {/* <FormLabel htmlFor='contactNumber'>Contact Number</FormLabel> */}
                <TextField
                fullWidth
                variant='outlined'
                type='tel'
                id='contactNumber'
                label="Contact Number"
                placeholder='Enter contact number'
                name="contactNumber"
                value={inputValue.contact}
                onChange={(e)=>handelOnChange('contact', e.target.value)}
                // error
                // helperText="This field is required."
                />
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                {/* <InputLabel htmlFor="city">City</InputLabel> */}
                <Select 
                className='textcapitalize'
                fullWidth
                variant='outlined'
                id='city'
                label="Select City"
                placeholder='Select City'
                name="city"
                value={inputValue.city} 
                onChange={(e)=>handelOnChange('city', e.target.value)}
                // error
                // helperText="This field is required."
                >
                    <MenuItem value="" disabled>Select City</MenuItem>
                    {cities.map((cityName, index) => (
                        <MenuItem className='textcapitalize' key={index} value={cityName}>{cityName}</MenuItem>
                    ))}
                </Select>
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='dateOfBirth'>D.O.B</FormLabel>
                <TextField
                fullWidth
                variant='outlined'
                type='date'
                id='dateOfBirth'
                label="Date of birth"
                placeholder='Select D.O.B'
                name="dateOfBirth"
                value={inputValue.dob}
                onChange={(e)=>handelOnChange('dob', e.target.value)}
                // error
                // helperText="This field is required."
                />
            </FormControl>
            {/* <InputLabel>D.O.B</InputLabel> */}
            {/* <LocalizationProvider dateAdapter={AdapterDayjs}>
                <DatePicker value={inputValue.dob} onChange={(e)=>handelOnChange('dob', e.$D)} />
            </LocalizationProvider> */}
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormLabel htmlFor='skill'>Skill</FormLabel>
                <Select
                className='textcapitalize'
                fullWidth
                variant='outlined'
                id='skill'
                // label="Select Skills"
                // placeholder='Select Skills'
                name="skill"
                value={inputValue.skill} 
                onChange={(e)=>handelOnChange('skill', e.target.value)}
                // error
                // helperText="This field is required."
                >
                    <MenuItem selected={true} disabled>Select Skill</MenuItem>
                    {skills.map((skill, index)=>(
                        <MenuItem className='textcapitalize' key={index} value={skill}>{skill}</MenuItem>
                    ))}
                </Select>
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <RadioGroup 
                row
                name="gender"
                value={inputValue.gender}
                onChange={(e)=>handelOnChange('gender', e.target.value)}
                // error
                // helperText="This field is required."
                >
                    <FormControlLabel value="female" control={<Radio />} label="Female" />
                    <FormControlLabel value="male" control={<Radio />} label="Male" />
                    <FormControlLabel value="other" control={<Radio />} label="Other" />
                </RadioGroup>
            </FormControl>
        </div>
        <div className={styles.formRow}>
            <FormControl>
                <FormControlLabel 
                control={<Checkbox />} 
                label="This is active user" 
                name="isActive"
                value={inputValue.isActive}
                onChange={(value)=>handelOnChange('isActive', value.target.checked)}
                // error
                // helperText="This field is required."
                />
            </FormControl>
        </div>
      </div>
    </form>
  )
}

export default EmployeeForm
