import React from 'react';

const MessageList = (props) => {
    const {todoList} = props;

    const handelDelete = (_id) => {
        const indexValue = todoList.findIndex((data)=>(data.id === _id))
        todoList.splice(indexValue, 1)
        // setTodoList([...todoList])
        // setSuccessMsg(true)
        // setTimeout(() => {
        //     setSuccessMsg(false)  
        // }, 3000);
    }

    const handelEdit = (_id) => {
        const getValue = todoList.find((data)=>(data.id === _id))
        // setInputValue(getValue.message)
        // setId(getValue.id)
        // console.log(_id)
    }

    return(
        <>
            {todoList?.length !== 0 ? (
            <ol>
                {}
                {
                    todoList?.map((value, index)=>(
                    <li key={value.id}>
                        <span>{value.message}</span>
                        <span>
                            <button onClick={()=>handelDelete(value.id)}>Delete</button>
                            <button onClick={()=>handelEdit(value.id)}>Edit</button>
                        </span>
                    </li>
                    )) 
                }
            </ol>
            ) : (
                <h4>There is no data</h4>
            )}
        </>
    )
}

export default MessageList
